package cy.its.trafficSituation.rest.action.impl;

import java.util.HashMap;
import java.util.List;

import javax.servlet.http.HttpServletRequest;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.web.bind.annotation.ModelAttribute;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RequestParam;
import org.springframework.web.bind.annotation.RestController;

import com.alibaba.fastjson.JSONObject;
import com.github.pagehelper.Page;
import com.github.pagehelper.PageHelper;

import cy.its.trafficSituation.domain.model.TrafficAlarmConfig;
import cy.its.trafficSituation.domain.service.ITrafficAlarmConfigService;
import cy.its.trafficSituation.rest.action.ITrafficAlarmConfigAction;
import cy.its.trafficSituation.rest.dto.TrafficAlarmConfigDto;

@RestController
@RequestMapping("/trafficSituation/alarmConfig")
public class TrafficAlarmConfigAction implements ITrafficAlarmConfigAction {

	@Autowired
	ITrafficAlarmConfigService trafficAlarmConfigService;

	@RequestMapping("/add")
	public String add(@ModelAttribute(value = "trafficAlarmConfigDto") TrafficAlarmConfigDto trafficAlarmConfigDto) {
		List<TrafficAlarmConfig> configs=trafficAlarmConfigService.selectByType(trafficAlarmConfigDto.getAlarmValueType());
		boolean isExist=false;
		for (TrafficAlarmConfig config : configs) {
			if(config.getAlarmGrade().equals(trafficAlarmConfigDto.getAlarmGrade())&&
					config.getRoadType().equals(trafficAlarmConfigDto.getRoadType())){
				isExist=true;
			}
		}
		boolean isContain=false;
		if(!isExist){
			float min=Float.parseFloat(trafficAlarmConfigDto.getMin());
			float max=Float.parseFloat(trafficAlarmConfigDto.getMax());
			for (TrafficAlarmConfig config : configs) {
				//道路等级相同时
				if(config.getRoadType().equals(trafficAlarmConfigDto.getRoadType())){
					float tempMin=Float.parseFloat(config.getMin());
					float tempMax=Float.parseFloat(config.getMax());
					if(min>tempMin && min<tempMax){
						isContain=true;
					}
					if(max>tempMin && max<tempMax){
						isContain=true;
					}
				}
			}
		}
		if(isExist){
			return "exist";
		}else{
			if(isContain){
				return "contain";
			}
			else{
				return trafficAlarmConfigService.save(trafficAlarmConfigDto.parseToTrafficAlarmConfig());
			}
		}
	}
	                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           

	@RequestMapping("/delete")
	public int delete(@RequestParam(value = "id", required = true) String id) {
		return trafficAlarmConfigService.delete(id);
	}

	@RequestMapping("/deleteMultiple")
	public int deleteMultiple(@RequestParam(value = "ids", required = true) String ids) {
		int count = 0;
		String[] arr = ids.split(",");
		for (String id : arr) {
			count += trafficAlarmConfigService.delete(id);
		}
		return count;
	}

	@RequestMapping("/update")
	public String update(@ModelAttribute(value = "trafficAlarmConfigDto") TrafficAlarmConfigDto trafficAlarmConfigDto) {
		List<TrafficAlarmConfig> configs=trafficAlarmConfigService.selectByType(trafficAlarmConfigDto.getAlarmValueType());		
		boolean isExist=false;
		for (TrafficAlarmConfig config : configs) {
			if(!config.getAlarmValueId().equals(trafficAlarmConfigDto.getAlarmValueId())){
				if(config.getAlarmGrade().equals(trafficAlarmConfigDto.getAlarmGrade())&&
						config.getRoadType().equals(trafficAlarmConfigDto.getRoadType())){
					isExist=true;
				}
			}
		}
		
		boolean isContain=false;
		if(!isExist){
			float min=Float.parseFloat(trafficAlarmConfigDto.getMin());
			float max=Float.parseFloat(trafficAlarmConfigDto.getMax());
			for (TrafficAlarmConfig config : configs) {
				if(!config.getAlarmValueId().equals(trafficAlarmConfigDto.getAlarmValueId())){
					if(config.getRoadType().equals(trafficAlarmConfigDto.getRoadType())){
						float tempMin=Float.parseFloat(config.getMin());
						float tempMax=Float.parseFloat(config.getMax());
						if(min>tempMin && min<tempMax){
							isContain=true;
						}
						if(max>tempMin && max<tempMax){
							isContain=true;
						}
					}
				}
			}
		}
		if(isExist){
			return "exist";
		}else{
			if(isContain){
				return "contain";
			}
			else{
				return trafficAlarmConfigService.update(trafficAlarmConfigDto.parseToTrafficAlarmConfig())+"";
			}
		}
	}

	@RequestMapping("/selectAll")
	public Object selectAll(HttpServletRequest request) {	
		Integer pageNow = Integer.valueOf(request.getParameter("pageNumber"));
		Integer pageSize = Integer.valueOf(request.getParameter("pageSize"));
		PageHelper.startPage(pageNow, pageSize);
		Page pageRs = (Page) trafficAlarmConfigService.selectAll(new HashMap());

		JSONObject jsonObj = new JSONObject();
		jsonObj.put("error", "");
		JSONObject jsonObject = new JSONObject();
		jsonObject.put("total", pageRs.getTotal());
		jsonObject.put("rows", pageRs.getResult());
		jsonObj.put("result", jsonObject);
		return jsonObj;
	}

}
