CREATE OR REPLACE Function Getgcxxjsl
(
  In_Gcsj In Date, In_Rksj In Date
) Return Varchar2 Is
Begin
  If In_Rksj < In_Gcsj Then
    Return 'WC';
  End If;
  If In_Rksj - In_Gcsj <= 8 / (24 * 60 * 60) Then
    Return '8S';
  End If;
  If In_Rksj - In_Gcsj <= 30 / (24 * 60 * 60) Then
    Return '30S';
  End If;

  If In_Rksj - In_Gcsj <= 60 / (24 * 60 * 60) Then
    Return '60S';
  End If;

  If In_Rksj - In_Gcsj <= 5 / (24 * 60) Then
    Return '5M';
  End If;

  If In_Rksj - In_Gcsj <= 5 / (24 * 10) Then
    Return '30M';
  End If;

  Return '30OM';

End Getgcxxjsl;
/

