CREATE OR REPLACE Function Trans_Bkcl(In_Bkcl In Varchar) Return Varchar Is
	Ret        Varchar2(2000);
	Record_Obj Split_Type;
	Icount     Number;
Begin
	Ret := '';
	For Record_Obj In (Select * From Table(Split(In_Bkcl, ';'))) Loop
		Select Count(1) Into Icount From Table(Split(Record_Obj.s_Value, ','));
		If Icount <= 5 Then
			Ret := Ret || ';' || Record_Obj.s_Value || ',0,1';
		Else
			Ret := Ret || ';' || Record_Obj.s_Value;
		End If;
	End Loop;
	If Length(Ret) > 1 Then
		Ret := Substr(Ret, 2);
	End If;
	Return Ret;
End Trans_Bkcl;
/

