  Loop
    Fetch c_Tab
      Into Index_Obj;
    Exit When c_Tab%Notfound;
    Vsql := 'drop index ' || Index_Obj.Index_Name;
    Execute Immediate Vsql;
  End Loop;
End Drop_Index;
