prompt
CREATE OR REPLACE Procedure Initcheckmode Is
  Jrdbh_ Varchar2(16);
  Icount Number;
Begin

  -- 比对模式
  Icount := 0;
  Select Count(1) Into Icount From Dev_Tollgate_Check_Mode Where Bdms = 0;

  If Icount = 0 Then
    Insert Into Dev_Tollgate_Check_Mode (Bdms, Bdmsmc, Bdlx, Cjr, Cjsj) Values (0, '全部比对', '*', '系统', Sysdate);
  Else
    Update Dev_Tollgate_Check_Mode Set Bdmsmc = '全部比对', Bdlx = '*', Csbj = '0', Bjcsbj = '0' Where Bdms = 0;
  End If;

  -- 公安网接入点
  Icount := 0;
  Select Count(1) Into Icount From Dev_Mount;

  If Icount = 1 Then
    Select Nvl(Jrdbh, 'NONE') Into Jrdbh_ From Dev_Mount Where Rownum = 1;

    If 'NONE' <> Jrdbh_ Then
      Update Dev_Tollgate
      Set Jrdbh = Jrdbh_, Csbj = '0', Bjcsbj = '0', Jyw = Dev_Pkg.Gettollgatejyw(Kkbh, Kklx, Kkxz, Glbm, Dldm, Kkzt)
      Where Jrdbh Is Null;
    End If;
  End If;

  -- 预警参数
  Insert Into Dev_Tollgate_Config
    (Kkbh, Jrdbh, Glbm, Sfbd, Bdms, Jsbm, Wfcs, Qsbm, Bdhpzl)
    Select Kkbh, Jrdbh, Glbm, '1' "SDBD", 0 "BDMS", Glbm "JSBM", 10 "WFCS", Glbm "QSBM", '1' "BDHPZL"
    From Dev_Tollgate
    Where Kkbh Not In (Select Kkbh From Dev_Tollgate_Config) And Jrdbh Is Not Null;

  Commit;
End;
/

