					 Dev_Tollgate_Config_Obj.Bdhpzl, Dev_Tollgate_Config_Obj.Cjsj, Sysdate, '2');
			Else
				Delete From Dev_Tollgate_Config Where Kkbh = v_Kkbh;
				Insert Into Dev_Tollgate_Config
					(Kkbh, Jrdbh, Glbm, Sfbd, Bdms, Jsbm, Wfcs, Qsbm, Bz, Bdhpzl, Cjsj, Gxsj, Fxlx)
				Values
					(Dev_Tollgate_Config_Obj.Kkbh, Dev_Tollgate_Config_Obj.Jrdbh, Dev_Tollgate_Config_Obj.Glbm,
					 Dev_Tollgate_Config_Obj.Sfbd, Dev_Tollgate_Config_Obj.Bdms, Dev_Tollgate_Config_Obj.Jsbm,
					 Dev_Tollgate_Config_Obj.Wfcs, Dev_Tollgate_Config_Obj.Qsbm, Dev_Tollgate_Config_Obj.Bz,
					 Dev_Tollgate_Config_Obj.Bdhpzl, Dev_Tollgate_Config_Obj.Cjsj, Sysdate, '1');
				Insert Into Dev_Tollgate_Config
					(Kkbh, Jrdbh, Glbm, Sfbd, Bdms, Jsbm, Wfcs, Qsbm, Bz, Bdhpzl, Cjsj, Gxsj, Fxlx)
				Values
					(Dev_Tollgate_Config_Obj.Kkbh, Dev_Tollgate_Config_Obj.Jrdbh, Dev_Tollgate_Config_Obj.Glbm,
					 Dev_Tollgate_Config_Obj.Sfbd, Dev_Tollgate_Config_Obj.Bdms, Dev_Tollgate_Config_Obj.Jsbm,
					 Dev_Tollgate_Config_Obj.Wfcs, Dev_Tollgate_Config_Obj.Qsbm, Dev_Tollgate_Config_Obj.Bz,
					 Dev_Tollgate_Config_Obj.Bdhpzl, Dev_Tollgate_Config_Obj.Cjsj, Sysdate, '2');
