prompt Creating procedure RENAME_TABLE
prompt ===============================
prompt
CREATE OR REPLACE Procedure Rename_Table
(
	In_Oldname In Varchar2, In_Newname In Varchar2, In_Suffix In Varchar2
) Is
	Type Cur_Type Is Ref Cursor;
	c_Tab          Cur_Type;
	Icount         Number;
	Vsql           Varchar2(2000);
	Index_Obj      User_Indexes%Rowtype;
	Constraint_Obj User_Constraints%Rowtype;
	Vsuffix        Varchar2(200);
Begin
	Vsuffix := In_Suffix;
	If Vsuffix Is Null Then
		Vsuffix := '_X';
	End If;
	Vsql := 'select count(1) from user_tables where table_name=''' || Upper(In_Oldname) ||
					''' and table_name not like ''%PHOTO%''';
	Execute Immediate Vsql
		Into Icount;

	If Icount > 0 Then
		Vsql := 'Select *
		From User_Indexes
		Where Table_Name = ''' || Upper(In_Oldname) || '''';
		Open c_Tab For Vsql;
		Loop
			Fetch c_Tab
				Into Index_Obj;
			Exit When c_Tab%Notfound;
			Vsql := 'alter index ' || Index_Obj.Index_Name || ' rename to ' || Replace(Index_Obj.Index_Name, 'RECG_', 'R_') || 'X';
			Execute Immediate Vsql;
		End Loop;
		Close c_Tab;

		Vsql := 'Select *
												 From User_Constraints
												 Where Table_Name = ''' || Upper(In_Oldname) || ''' And Constraint_Name Not Like ''SYS%''';
		Open c_Tab For Vsql;
		Loop
			Fetch c_Tab
				Into Constraint_Obj;
			Exit When c_Tab%Notfound;
			Vsql := 'alter table ' || In_Oldname || ' rename constraint ' || Constraint_Obj.Constraint_Name || ' to ' ||
							Constraint_Obj.Constraint_Name || 'X';
			Execute Immediate Vsql;
		End Loop;

		Close c_Tab;

		Vsql := 'rename ' || In_Oldname || ' to ' || In_Newname || Vsuffix;
		Execute Immediate Vsql;
	End If;
End Rename_Table;
/

