				Into Index_Obj;
			Exit When c_Tab%Notfound;
			Vsql := 'alter index ' || Index_Obj.Index_Name || ' rename to ' || Index_Obj.Index_Name || '_O';
			Execute Immediate Vsql;
		End Loop;
		Close c_Tab;

