	End If;

	Open Cur_Alarm_Seize For 'select * from ' || Vtable;
	Loop
		Fetch Cur_Alarm_Seize
			Into Vmc_Seize_Vehicle_Object;
		Exit When Cur_Alarm_Seize%Notfound;

		If Vmc_Seize_Vehicle_Object.Chjg Is Not Null Then
			Vljclqk := '';
			Vwchyy := '';
			Vchjg := '';
			Vljmj := '';
			Vchbm := '';
			If Vmc_Seize_Vehicle_Object.Chjg = '1' Then
				Vljclqk := '1';
				Vchjg := '1';
				Vljmj := Vmc_Seize_Vehicle_Object.Ljmj;
				Vchbm := Vmc_Seize_Vehicle_Object.Chbm;

