prompt
CREATE OR REPLACE Package Body City_Bdm_Pkg Is
	--归并时间,第二个参数为分钟
	Function Grouptime
	(
		Realtime Date, Groupperiod Number
	) Return Number Is
		Ret Number;
	Begin
		Ret := Trunc(To_Number(To_Char(Realtime, 'yyyymmddhh24mi')) / Groupperiod) * Groupperiod;
		Return Ret;
	End;
	--判断日期是否符合条件，kssj<日期<jssj, kssd<日期.时间<jssd
	Function Checktime
	(
		Checkdate Date, Kssj Date, Jssj Date, Kssd Varchar2, Jssd Varchar2
	) Return Number Is
		Ret Number;
	Begin
		Ret := 1;
		If Checkdate <= Kssj Then
			Ret := 0;
		End If;
		If Checkdate >= Jssj Then
			Ret := 0;
		End If;
		If To_Char(Checkdate, 'HH24:mi:ss') <= Kssd Then
			Ret := 0;
		End If;
		If To_Char(Checkdate, 'HH24:mi:ss') >= Jssd Then
			Ret := 0;
		End If;
		Return Ret;
	End;
	--根据路口路段配置表的序号（包含方向）获取到交通流的相关设备编号（包含卡口和流量检测设备）
	Function Getsbbhitems
	(
		In_Xh Varchar2, In_Fx Varchar2, In_Glbmhead Varchar2
	) Return Varchar2 Is
		i_Sbbh Varchar2(1000);
		v_Sql  Varchar2(1000);
		v_Sql1 Varchar2(1000);
		v_Sql2 Varchar2(1000);
		v_Dldm Varchar2(5);
		v_Tmp  Varchar2(50);
	Begin
		Begin
			v_Sql := 'Select dldm from rm_roadflow_config where xh = ''' || In_Xh || '''';
			Execute Immediate v_Sql
				Into v_Dldm;
		Exception
			When No_Data_Found Then
				i_Sbbh := '';
				Return i_Sbbh;
		End;

		v_Tmp := '';
		If Substr(v_Dldm, 1, 1) = '5' Or Substr(v_Dldm, 1, 1) = '6' Or Substr(v_Dldm, 1, 1) = '7' Or
			 Substr(v_Dldm, 1, 1) = '8' Then
			v_Tmp := 'and a.lkh = t.lddm ';
		End If;

		v_Sql := 'Select Sbbh
From Dev_Flow_Equipment a
Inner Join (Select Glbm, Dldm, Lddm, Xzqh,sflk From Rm_Roadflow_Config Where Xh = ''' || In_Xh ||
						 ''') t
On A.Glbm like ''' || In_Glbmhead || '%'' And A.Dldm = T.Dldm and a.xzqh=t.xzqh ' || v_Tmp ||
						 ' and a.fxjclx=''' || In_Fx || '''';
		v_Sql1 := 'Select kkbh
From dev_tollgate a
Inner Join (Select Glbm, Dldm, Lddm, Xzqh,sflk From Rm_Roadflow_Config Where Xh = ''' || In_Xh ||
							''') t
On A.Glbm like ''' || In_Glbmhead || '%'' And A.Dldm = T.Dldm and a.xzqh=t.xzqh ' || v_Tmp;
		--由于卡口没有设置明确的上行和下行方向字段，所以不过滤方向条件，在卡口流量表中进行过滤 || ' and a.fxlx=''' || In_Fx || '''';
		Begin
			v_Sql2 := 'select wm_concat(sbbh) from (' || v_Sql || ' union all ' || v_Sql1 || ')';
			Execute Immediate v_Sql2
				Into i_Sbbh;
		Exception
			When No_Data_Found Then
				i_Sbbh := '';
				Return i_Sbbh;
		End;
		Return i_Sbbh;
	End;

