			 Sysdate, Sysdate, City_Ledger_Police_Obj.Bz, '0', '0');
		Merge Into City_Ledger_Police_His p
		Using (With a As (Select Glbm, Bmjb, Sjbm
											From Frm_Department t
											Where t.Sjbm = City_Ledger_Police_Obj.Glbm And
														t.Bmjb = (Select (x.Bmjb + 1) From Frm_Department x Where x.Glbm = t.Sjbm))
