			Group By Sjbm) Rs On (p.Glbm = Rs.Glbm) When Matched Then
				Update Set p.Zods = Rs.Zods, p.Dds = Rs.Dds, p.Zds = Rs.Zds;


		Update City_Ledger_Police_His
		Set Dzysjls = (Select Sum(Case
																 When Xl < 31 Then
																	1
															 End) Dzysmjs
										From Bas_Police
										Where Bmdm = City_Ledger_Police_Obj.Glbm)
		Where Glbm = City_Ledger_Police_Obj.Glbm;

		Out_Jg := 1;
		Out_Msg := 'success';
	End;

