	Procedure Dopassstat
	(
		Out_Jg Out Number, Out_Msg Out Varchar2
	) Is
		--20150303 kongcc新增,生成已联网卡口
		Cursor c_Kksx Is
			Select Kkbh, Substr(Tjsd, 1, 6) Gcsd, Sum(Txclzs) Gczs
			From Stat_Pass_Hour
			Where Kkbh Not In (Select Lx1 From Stat_Suspect Where Fl = 'YLWK')
			Group By Kkbh, Substr(Tjsd, 1, 6);
		v_Kkbh  Varchar2(12);
		v_Gcsd  Varchar2(6);
		v_Gczs  Number;
		v_Count Number;
		v_Glbm  Varchar(12);
		--结束

		v_Starttime Date;
		v_Hs        Number;

	Begin
		Update Rm_Args Set Csz = '1' Where Gjz = 'PASS_STAT_LOCK';
		If Sql%Notfound Then
			Out_Jg := -1;
			Out_Msg := '未找到过车统计锁';
			Return;
		End If;

		Select Sysdate Into v_Starttime From Dual;
		--20150303 kongcc新增,生成已联网卡口
		Open c_Kksx;
		Loop
			Fetch c_Kksx
				Into v_Kkbh, v_Gcsd, v_Gczs;
			Exit When c_Kksx%Notfound;
			If v_Gczs >= 1000 Then
				Select Count(1)
				Into v_Count
				From Dev_Tollgate
				Where Kkbh = v_Kkbh And Kkzt <> '2' And Kkbh Not In (Select Lx1 From Stat_Suspect Where Fl = 'YLWK');
				If v_Count > 0 Then
					Select Glbm Into v_Glbm From Dev_Tollgate Where Kkbh = v_Kkbh And Kkzt <> '2';
					Insert Into Stat_Suspect
						(Glbm, Tjsd, Fl, Lx1, Lx2, Lx3, Lx4, Tjsl, Gxsj, Csbj, Bjcsbj, Bz)
					Values
						(v_Glbm, To_Char(Sysdate, 'yyyymmdd'), 'YLWK', v_Kkbh, '-', '-', '-', 0, Sysdate, '0', '0', '');
				End If;
			End If;
		End Loop;
		Close c_Kksx;
		--结束
		v_Hs := Trunc((Sysdate - v_Starttime) * 86400);
		Commit;
		Out_Jg := 1;
		Out_Msg := '已联网卡口信息统计完成，统计至当天。（耗时：' || v_Hs || '秒）';
	Exception
		When Others Then
			Out_Jg := Sqlcode;
			Out_Msg := '已联网卡口信息统计失败！' || Sqlerrm;
			Rollback;
	End;

