      Where Fkzt <> '0' And Fksj >= Kssj And Fksj < Jssj
      Union All
      Select Glbm, Sbbh, Yjsj, Bklx, Bkzlx, Fkzt, Ljzt, Fksj, Wchyy
      From Vmc_Alarm_Pass
      Where Fkzt <> '0' And Fksj >= Kssj And Fksj < Jssj;
    v_Laststat  Varchar2(8);
    v_Startdate Date;
    v_Enddate   Date;

