        --及时签收
        If ((v_Qssj - v_Yjsj) * 86400) <= 600 Then
          Addstatalarm(v_Glbm, v_Kkbh, v_Tjrq, 'JSQS', '-', '-', '');
        End If;
      End If;
    End Loop;
    Close c_Alarm_Sure;

    Open c_Alarm_Handle(v_Startdate, v_Enddate);
    Loop
      Fetch c_Alarm_Handle
        Into v_Glbm, v_Kkbh, v_Yjsj, v_Bklx, v_Bkzlx, v_Fkzt, v_Ljzt, v_Fksj, v_Wchyy;
      Exit When c_Alarm_Handle%Notfound;
      v_Tjrq := To_Char(v_Yjsj, 'yyyymmdd');
      If v_Bkzlx Is Null Then
        v_Bkzlx := '-';
      End If;
      If Length(v_Kkbh) = 12 Then
        --反馈状态
        Addstatalarm(v_Glbm, v_Kkbh, v_Tjrq, 'FKZT', v_Fkzt, '-', '');
