    Insert Into Stat_Alarm_Business
      (Glbm, Kkbh, Tjsd, Lx1, Lx2, Lx3, Tjsl, Gxsj, Csbj, Bjcsbj, Bz)
    Values
      (v_Djglbm, '-', To_Char(v_Enddate, 'yyyymmdd'), 'RKSJ', '-', '-', v_Hs, Sysdate, '0', '0', '');

    Commit;
    Out_Jg := 1;
    Out_Msg := '操作成功，统计至' || To_Char(v_Enddate - 1, 'yyyymmdd') || '，耗时（秒）：' || v_Hs;
