	Procedure Addstatalarmseized
	(
		v_Glbm Varchar2, v_Sbzl Varchar2, v_Sblx Varchar2, v_Sbbh Varchar2, v_Dldm Varchar2, v_Tjsd Varchar2, v_Lx1 Varchar2,
		v_Lx2 Varchar2, v_Lx3 Varchar2, v_Lx4 Varchar2, v_Lx5 Varchar2, v_Lx6 Varchar2, v_Czr Varchar2, v_Bz Varchar2,
		In_Ywlb In Varchar2
	) Is
	Begin
		Update Stat_Center_Alarm
		Set Tjsl = Tjsl + 1, Csbj = '0', Bjcsbj = '0', Gxsj = Sysdate
		Where Glbm = v_Glbm And Sbzl = v_Sbzl And Sblx = v_Sblx And Sbbh = v_Sbbh And Dldm = v_Dldm And Tjsd = v_Tjsd And
					Lx1 = v_Lx1 And Lx2 = v_Lx2 And Lx3 = v_Lx3 And Lx4 = v_Lx4 And Lx5 = v_Lx5 And Lx6 = v_Lx6 And Czr = v_Czr;
		If Sql%Notfound Then
			Insert Into Stat_Center_Alarm
				(Glbm, Sbzl, Sblx, Sbbh, Dldm, Tjsd, Lx1, Lx2, Lx3, Lx4, Lx5, Lx6, Czr, Tjsl, Gxsj, Csbj, Bjcsbj, Bz)
			Values
				(v_Glbm, v_Sbzl, v_Sblx, v_Sbbh, v_Dldm, v_Tjsd, v_Lx1, v_Lx2, v_Lx3, v_Lx4, v_Lx5, v_Lx6, v_Czr, 1, Sysdate,
				 '0', '0', v_Bz);
		End If;
		If (In_Ywlb = '4') Then
			Update Stat_Recg_Center_Alarm
			Set Tjsl = Tjsl + 1, Csbj = '0', Bjcsbj = '0', Gxsj = Sysdate
			Where Glbm = v_Glbm And Sbzl = v_Sbzl And Sblx = v_Sblx And Sbbh = v_Sbbh And Dldm = v_Dldm And Tjsd = v_Tjsd And
						Lx1 = v_Lx1 And Lx2 = v_Lx2 And Lx3 = v_Lx3 And Lx4 = v_Lx4 And Lx5 = v_Lx5 And Lx6 = v_Lx6 And Czr = v_Czr;
			If Sql%Notfound Then
				Insert Into Stat_Recg_Center_Alarm
					(Glbm, Sbzl, Sblx, Sbbh, Dldm, Tjsd, Lx1, Lx2, Lx3, Lx4, Lx5, Lx6, Czr, Tjsl, Gxsj, Csbj, Bjcsbj, Bz)
				Values
					(v_Glbm, v_Sbzl, v_Sblx, v_Sbbh, v_Dldm, v_Tjsd, v_Lx1, v_Lx2, v_Lx3, v_Lx4, v_Lx5, v_Lx6, v_Czr, 1, Sysdate,
					 '0', '0', v_Bz);
			End If;
		End If;

	End;
	Procedure Addvehhandlebusi
	(
		In_Tjrq In Varchar2, In_Glbm In Varchar2, In_Fwzbh In Varchar2, In_Fwzlx In Varchar2, In_Dldm In Varchar2,
		In_Fl In Varchar2, In_Lx1 In Varchar2, In_Lx2 In Varchar2, In_Czr In Varchar2, In_Bz In Varchar2
	) Is
	Begin
		Update Stat_Station_Alarm
		Set Sl = Sl + 1, Csbj = '0', Bjcsbj = '0', Gxsj = Sysdate
		Where Tjrq = In_Tjrq And Glbm = In_Glbm And Fwzbh = In_Fwzbh And Fwzlx = In_Fwzlx And Dldm = In_Dldm And Fl = In_Fl And
					Lx1 = In_Lx1 And Lx2 = In_Lx2 And Bz = In_Bz And Czr = In_Czr;
