			Insert Into Stat_Station_Alarm
				(Tjrq, Glbm, Fwzbh, Fwzlx, Dldm, Fl, Lx1, Lx2, Czr, Sl, Bz)
			Values
				(In_Tjrq, In_Glbm, In_Fwzbh, In_Fwzlx, In_Dldm, In_Fl, In_Lx1, In_Lx2, In_Czr, 1, In_Bz);
		End If;
	End;
