		v_Bkzlx  Varchar2(2);
		v_Bksjly Varchar2(1);
		v_Bkbj   Varchar2(1);
		v_Xcbj   Varchar2(1);
		v_Bkjz   Varchar2(2);
		v_Smcl   Varchar2(1);
		v_Cls    Number;
		v_Ywlx   Varchar2(2);
		v_Bkfwlx Varchar2(1);

		v_Ckjg Varchar2(12);
		v_Cksj Date;
		v_Cklx Varchar2(1);
		v_Ckfs Varchar2(1);

		v_Fzjg   Varchar2(15);
		v_Rksj   Date;
		v_Qssj   Date;
		v_Sjc    Number;
		v_Count1 Number;
		v_Count2 Number;
		v_Hs     Number;

	Begin
		Update Rm_Args Set Csz = '1' Where Gjz = 'SUSP_STAT_LOCK';
		If Sql%Notfound Then
			Out_Jg := -1;
			Out_Msg := '未找到布控信息统计锁';
			Return;
		End If;
		v_Xtyxms := Frm_Comm_Pkg.Getsysparavalue('60', 'XTYXMS');
		Select Mrz Into v_Bdfzjg From Frm_Syspara Where Gjz = 'BDFZJG';
		Select Sysdate Into v_S1 From Dual;
		Select Nvl(Max(Tjsd), '20140101') Into v_Laststat From Stat_Suspect_Vehicle Where Fl = 'TJSD';

		v_Startdate := To_Date(v_Laststat || '000000', 'yyyymmddhh24miss');
		v_Enddate := Trunc(Sysdate, 'dd');

		If v_Startdate >= v_Enddate Then
			Out_Jg := 1;
			v_Hs := 0;
			Out_Msg := '布控信息统计完成，统计至' || To_Char(v_Enddate - 1, 'yyyymmdd') || '。（耗时：' || v_Hs || '秒）';
			Return;
		End If;

		Open c_Zhpt_Fake(v_Startdate, v_Enddate);
		Loop
			Fetch c_Zhpt_Fake
				Into v_Bkjg, v_Bksj, v_Bklx, v_Bksjly, v_Xcbj;
			Exit When c_Zhpt_Fake%Notfound;
			v_Tjrq := To_Char(v_Bksj, 'yyyymmdd');
			If Substr(v_Bkjg, 11, 2) = '10' Then
				v_Tjjg := Substr(v_Bkjg, 1, 8) || '0010';
			Else
				v_Tjjg := Substr(v_Bkjg, 1, 8) || '0000';
			End If;
			--套牌已布控
			Addstatsuspmid(v_Tjjg, v_Tjrq, 'TPBK', v_Bksjly, v_Bklx, v_Xcbj, '-', 1, '');
			Addstatsuspmx(v_Bkjg, v_Tjrq, 'TPBK', v_Bksjly, v_Bklx, v_Xcbj, '-', 1, '');
		End Loop;
		Close c_Zhpt_Fake;

		Open c_Zhpt_Yqs(v_Startdate, v_Enddate);
		Loop
			Fetch c_Zhpt_Yqs
				Into v_Xcdjg, v_Xcbj, v_Rksj;
			Exit When c_Zhpt_Yqs%Notfound;
			v_Tjrq := To_Char(v_Rksj, 'yyyymmdd');
			If Length(v_Xcdjg) > 2 Then
				Select Count(1)
				Into v_Count1
				From (Select Glbm From Frm_Department Where Fzjg Like v_Xcdjg || '%' Order By Bmjb, Glbm)
				Where Rownum = 1;
				If v_Count1 = 1 Then
					Select Glbm
					Into v_Xcdbm
					From (Select Glbm From Frm_Department Where Fzjg Like v_Xcdjg || '%' Order By Bmjb, Glbm)
					Where Rownum = 1;
				End If;
			Else
				Select Count(1)
				Into v_Count1
				From (Select Glbm From Frm_Department Where Fzjg = v_Xcdjg Order By Bmjb, Glbm)
				Where Rownum = 1;
				If v_Count1 = 1 Then
					Select Glbm
					Into v_Xcdbm
					From (Select Glbm From Frm_Department Where Fzjg = v_Xcdjg Order By Bmjb, Glbm)
					Where Rownum = 1;
				End If;
			End If;
			If v_Count1 = 1 Then
				If Substr(v_Xcdbm, 11, 2) = '10' Then
					v_Tjjg := Substr(v_Xcdbm, 1, 8) || '0010';
				Else
					v_Tjjg := Substr(v_Xcdbm, 1, 8) || '0000';
				End If;
				--套牌应签收
				Addstatsuspmid(v_Tjjg, v_Tjrq, 'TPYQ', v_Xcbj, '-', '-', '-', 1, '');
				Addstatsuspmx(v_Xcdbm, v_Tjrq, 'TPYQ', v_Xcbj, '-', '-', '-', 1, '');
			End If;
		End Loop;
		Close c_Zhpt_Yqs;

		Open c_Zhpt_Qs(v_Startdate, v_Enddate);
		Loop
			Fetch c_Zhpt_Qs
				Into v_Xcdjg, v_Rksj, v_Xcbj, v_Qssj;
			Exit When c_Zhpt_Qs%Notfound;
			v_Tjrq := To_Char(v_Rksj, 'yyyymmdd');
			If Length(v_Xcdjg) > 2 Then
				Select Count(1)
				Into v_Count1
				From (Select Glbm From Frm_Department Where Fzjg Like v_Xcdjg || '%' Order By Bmjb, Glbm)
				Where Rownum = 1;
				If v_Count1 = 1 Then
					Select Glbm
					Into v_Xcdbm
					From (Select Glbm From Frm_Department Where Fzjg Like v_Xcdjg || '%' Order By Bmjb, Glbm)
					Where Rownum = 1;
				End If;
			Else
				Select Count(1)
				Into v_Count1
				From (Select Glbm From Frm_Department Where Fzjg = v_Xcdjg Order By Bmjb, Glbm)
				Where Rownum = 1;
				If v_Count1 = 1 Then
					Select Glbm
					Into v_Xcdbm
					From (Select Glbm From Frm_Department Where Fzjg = v_Xcdjg Order By Bmjb, Glbm)
					Where Rownum = 1;
				End If;
			End If;
			If v_Count1 = 1 Then
				If Substr(v_Xcdbm, 11, 2) = '10' Then
					v_Tjjg := Substr(v_Xcdbm, 1, 8) || '0010';
				Else
					v_Tjjg := Substr(v_Xcdbm, 1, 8) || '0000';
				End If;
				--套牌已签收
				Addstatsuspmid(v_Tjjg, v_Tjrq, 'TPQS', v_Xcbj, '-', '-', '-', 1, '');
				Addstatsuspmx(v_Xcdbm, v_Tjrq, 'TPQS', v_Xcbj, '-', '-', '-', 1, '');
			End If;
		End Loop;
		Close c_Zhpt_Qs;

		--部局下发重点车辆，用bkzlx区分未年检、未报废、违法未处理
		Open c_Zhpt_Key_Veh(v_Startdate, v_Enddate);
		Loop
			Fetch c_Zhpt_Key_Veh
				Into v_Bkjg, v_Bksj, v_Bklx, v_Bkzlx, v_Bksjly;
			Exit When c_Zhpt_Key_Veh%Notfound;
			v_Tjrq := To_Char(v_Bksj, 'yyyymmdd');
			If v_Bkzlx Is Null Then
				v_Bkzlx := '-';
			End If;
			If Substr(v_Bkjg, 11, 2) = '10' Then
				v_Tjjg := Substr(v_Bkjg, 1, 8) || '0010';
			Else
				v_Tjjg := Substr(v_Bkjg, 1, 8) || '0000';
			End If;
			Addstatsuspmid(v_Tjjg, v_Tjrq, 'KVEH', v_Bklx, v_Bkzlx, v_Bksjly, '-', 1, '');
			Addstatsuspmx(v_Bkjg, v_Tjrq, 'KVEH', v_Bklx, v_Bkzlx, v_Bksjly, '-', 1, '');
		End Loop;
		Close c_Zhpt_Key_Veh;
		--上级布控、协查地布控签收、人工布控
		Open c_Sj_Bk(v_Startdate, v_Enddate);
		Loop
			Fetch c_Sj_Bk
				Into v_Bkjg, v_Bksj, v_Bklx, v_Xcbj, v_Bksjly, v_Bkfwlx;
			Exit When c_Sj_Bk%Notfound;
			v_Tjrq := To_Char(v_Bksj, 'yyyymmdd');
			If v_Bkzlx Is Null Then
				v_Bkzlx := '-';
			End If;
			If Substr(v_Bkjg, 11, 2) = '10' Then
				v_Tjjg := Substr(v_Bkjg, 1, 8) || '0010';
			Else
				v_Tjjg := Substr(v_Bkjg, 1, 8) || '0000';
			End If;
			If v_Bksjly = '1' Then
				--人工布控
				Addstatsuspmid(v_Tjjg, v_Tjrq, 'RGBK', v_Bklx, v_Bkzlx, v_Bkfwlx, '-', 1, '');
				Addstatsuspmx(v_Bkjg, v_Tjrq, 'RGBK', v_Bklx, v_Bkzlx, v_Bkfwlx, '-', 1, '');
			Elsif v_Bksjly = '4' Then
				--协查地布控签收
				Addstatsuspmid(v_Tjjg, v_Tjrq, 'XCD', v_Bklx, v_Bkzlx, v_Bkfwlx, '-', 1, '');
				Addstatsuspmx(v_Bkjg, v_Tjrq, 'XCD', v_Bklx, v_Bkzlx, v_Bkfwlx, '-', 1, '');
			Elsif v_Bksjly = '2' Then
				--上级布控--总队布控
				Addstatsuspmid(v_Tjjg, v_Tjrq, 'ZDBK', v_Bklx, v_Bkzlx, v_Bkfwlx, '-', 1, '');
				Addstatsuspmx(v_Bkjg, v_Tjrq, 'ZDBK', v_Bklx, v_Bkzlx, v_Bkfwlx, '-', 1, '');
			Elsif v_Bksjly = '3' Then
				--上级布控--部局布控
				Addstatsuspmid(v_Tjjg, v_Tjrq, 'BJBK', v_Bklx, v_Bkzlx, v_Bkfwlx, '-', 1, '');
				Addstatsuspmx(v_Bkjg, v_Tjrq, 'BJBK', v_Bklx, v_Bkzlx, v_Bkfwlx, '-', 1, '');
			End If;
		End Loop;
		Close c_Sj_Bk;
		--撤控
		Open c_Ck(v_Startdate, v_Enddate);
		Loop
			Fetch c_Ck
				Into v_Ckjg, v_Cksj, v_Bklx, v_Cklx, v_Ckfs;
			Exit When c_Ck%Notfound;
			v_Tjrq := To_Char(v_Cksj, 'yyyymmdd');
			If Substr(v_Ckjg, 11, 2) = '10' Then
				v_Tjjg := Substr(v_Ckjg, 1, 8) || '0010';
			Else
				v_Tjjg := Substr(v_Ckjg, 1, 8) || '0000';
			End If;
			Addstatsuspmid(v_Tjjg, v_Tjrq, 'CK', v_Bklx, v_Cklx, v_Ckfs, '-', 1, '');
			Addstatsuspmx(v_Ckjg, v_Tjrq, 'CK', v_Bklx, v_Cklx, v_Ckfs, '-', 1, '');
		End Loop;
		Close c_Ck;

		--协查布控与总队审批应签收
		Open c_Susp_Flow_Yqs(v_Startdate, v_Enddate);
		Loop
			Fetch c_Susp_Flow_Yqs
				Into v_Jsjg, v_Rksj, v_Ywlx;
			Exit When c_Susp_Flow_Yqs%Notfound;
			v_Tjrq := To_Char(v_Rksj, 'yyyymmdd');

			If Length(v_Jsjg) > 2 Then
				Select Count(1)
				Into v_Count1
				From (Select Glbm From Frm_Department Where Fzjg Like v_Jsjg || '%' Order By Bmjb, Glbm)
				Where Rownum = 1;
				If v_Count1 = 1 Then
					Select Glbm
					Into v_Jsbm
					From (Select Glbm From Frm_Department Where Fzjg Like v_Jsjg || '%' Order By Bmjb, Glbm)
					Where Rownum = 1;
				End If;
			Else
				Select Count(1)
				Into v_Count1
				From (Select Glbm From Frm_Department Where Fzjg = v_Jsjg Order By Bmjb, Glbm)
				Where Rownum = 1;
				If v_Count1 = 1 Then
					Select Glbm
					Into v_Jsbm
					From (Select Glbm From Frm_Department Where Fzjg = v_Jsjg Order By Bmjb, Glbm)
					Where Rownum = 1;
				End If;
			End If;
			If v_Count1 = 1 And (v_Xtyxms <> '3' Or v_Ywlx <> '02') Then
				If Substr(v_Jsbm, 11, 2) = '10' Then
					v_Tjjg := Substr(v_Jsbm, 1, 8) || '0010';
				Else
					v_Tjjg := Substr(v_Jsbm, 1, 8) || '0000';
				End If;
				Addstatsuspmid(v_Tjjg, v_Tjrq, 'YWYQ', v_Ywlx, '-', '-', '-', 1, '');
			End If;
		End Loop;
		Close c_Susp_Flow_Yqs;

		--协查布控与总队审批已签收
		Open c_Susp_Flow_Qs(v_Startdate, v_Enddate);
		Loop
			Fetch c_Susp_Flow_Qs
				Into v_Jsjg, v_Rksj, v_Ywlx;
			Exit When c_Susp_Flow_Qs%Notfound;
			v_Tjrq := To_Char(v_Rksj, 'yyyymmdd');

			If Length(v_Jsjg) > 2 Then
				Select Count(1)
				Into v_Count1
				From (Select Glbm From Frm_Department Where Fzjg Like v_Jsjg || '%' Order By Bmjb, Glbm)
				Where Rownum = 1;
				If v_Count1 = 1 Then
					Select Glbm
					Into v_Jsbm
					From (Select Glbm From Frm_Department Where Fzjg Like v_Jsjg || '%' Order By Bmjb, Glbm)
					Where Rownum = 1;
				End If;
			Else
				Select Count(1)
				Into v_Count1
				From (Select Glbm From Frm_Department Where Fzjg = v_Jsjg Order By Bmjb, Glbm)
				Where Rownum = 1;
				If v_Count1 = 1 Then
					Select Glbm
					Into v_Jsbm
					From (Select Glbm From Frm_Department Where Fzjg = v_Jsjg Order By Bmjb, Glbm)
					Where Rownum = 1;
				End If;
			End If;

			If v_Count1 = 1 And (v_Xtyxms <> '3' Or v_Ywlx <> '02') Then
				If Substr(v_Jsbm, 11, 2) = '10' Then
					v_Tjjg := Substr(v_Jsbm, 1, 8) || '0010';
				Else
					v_Tjjg := Substr(v_Jsbm, 1, 8) || '0000';
				End If;
				Addstatsuspmid(v_Tjjg, v_Tjrq, 'YWQS', v_Ywlx, '-', '-', '-', 1, '');
			End If;
		End Loop;
		Close c_Susp_Flow_Qs;

		--从中间表写入业务表
		Open c_Mid;
		Loop
			Fetch c_Mid
				Into v_Mid;
			Exit When c_Mid%Notfound;
			Update Stat_Suspect_Vehicle
			Set Tjsl = Tjsl + v_Mid.Tjsl, Gxsj = Sysdate, Csbj = '0', Bjcsbj = '0'
			Where Glbm = v_Mid.Glbm And Tjsd = v_Mid.Tjsd And Fl = v_Mid.Fl And Lx1 = v_Mid.Lx1 And Lx2 = v_Mid.Lx2 And
						Lx3 = v_Mid.Lx3 And Lx4 = v_Mid.Lx4;
			If Sql%Notfound Then
				Insert Into Stat_Suspect_Vehicle
					(Glbm, Tjsd, Fl, Lx1, Lx2, Lx3, Lx4, Tjsl, Gxsj, Csbj, Bjcsbj, Bz)
				Values
					(v_Mid.Glbm, v_Mid.Tjsd, v_Mid.Fl, v_Mid.Lx1, v_Mid.Lx2, v_Mid.Lx3, v_Mid.Lx4, v_Mid.Tjsl, Sysdate, '0', '0',
					 v_Mid.Bz);
			End If;
		End Loop;
		Close c_Mid;

		v_Hs := Trunc((Sysdate - v_S1) * 86400);

		Select Mrz Into v_Djglbm From Frm_Syspara Where Gjz = 'DJGLBM';
		Insert Into Stat_Suspect_Vehicle
			(Glbm, Tjsd, Fl, Lx1, Lx2, Lx3, Lx4, Tjsl, Gxsj, Csbj, Bjcsbj, Bz)
		Values
			(v_Djglbm, To_Char(v_Enddate, 'yyyymmdd'), 'TJSD', '-', '-', '-', '-', v_Hs, Sysdate, '0', '0', '');

		Commit;

		v_Hs := Trunc((Sysdate - v_S1) * 86400);

		Out_Jg := 1;
		Out_Msg := '布控信息统计完成，统计至' || To_Char(v_Enddate - 1, 'yyyymmdd') || '。（耗时：' || v_Hs || '秒）';
	Exception
		When Others Then
			Out_Jg := Sqlcode;
			Out_Msg := '布控信息统计失败！' || Sqlerrm;
			Rollback;
	End;

	Procedure Updateviostatbusi
	(
		In_Tjrq In Varchar2, In_Glbm In Varchar2, In_Ywlx In Varchar2, In_Lx1 In Varchar2, In_Lx2 In Varchar2,
		In_Czr In Varchar2
	) Is
	Begin
		Update Vio_Stat_Business
		Set Sl = Sl + 1, Csbj = '0', Bjcsbj = '0', Gxsj = Sysdate
		Where Tjrq = In_Tjrq And Glbm = In_Glbm And Ywlx = In_Ywlx And Lx1 = Nvl(Trim(In_Lx1), '-') And
					Lx2 = Nvl(Trim(In_Lx2), '-') And Czr = Nvl(Trim(In_Czr), '-');
		If Sql%Notfound Then
			Insert Into Vio_Stat_Business
				(Tjrq, Glbm, Ywlx, Lx1, Lx2, Sl, Czr)
			Values
				(In_Tjrq, In_Glbm, In_Ywlx, Nvl(Trim(In_Lx1), '-'), Nvl(Trim(In_Lx2), '-'), 1, Nvl(Trim(In_Czr), '-'));
		End If;
	End;

	Procedure Doviobusistat
	(
		Out_Jg Out Number, Out_Msg Out Varchar2
	) Is
		Cursor c_Yx
		(
			Kssj Date, Jssj Date
		) Is
			Select Lrbm, Lrr, Lrsj, Xxly, Sxbm, Sxsj, Sxr, Shbm, Shsj, Shr
			From Vio_Surveil
			Where Scsj >= Kssj And Scsj < Jssj And Scbj = '1' And Lrsj Is Not Null And Sxsj Is Not Null And Shsj Is Not Null And
						Sxr Is Not Null And Sxbm Is Not Null;

		Cursor c_Wx
		(
			Kssj Date, Jssj Date
		) Is
			Select Lrbm, Lrr, Lrsj, Xxly, Sxbm, Sxsj, Sxr, Shbm, Shsj, Shr
			From Vio_Surveil_Examine_Err
			Where Gxsj >= Kssj And Gxsj < Jssj And Lrsj Is Not Null And Sxsj Is Not Null And Shsj Is Not Null And
						Sxr Is Not Null And Shr Is Not Null And Cwyy <> '已现场处罚';

		Cursor c_Sx_Cw
		(
			Kssj Date, Jssj Date
		) Is
			Select Lrbm, Lrr, Lrsj, Xxly, Sxbm, Sxsj, Sxr
			From Vio_Surveil_Screen_Err
			Where Gxsj >= Kssj And Gxsj < Jssj And Lrsj Is Not Null And Sxsj Is Not Null And Sxr Is Not Null And
						Cwyy <> '已现场处罚';

		v_Laststat  Varchar2(8);
		v_Startdate Date;
		v_Enddate   Date;
		v_Djglbm    Varchar2(12);
		v_Tjrq      Varchar2(8);
		v_S1        Date;
		v_Hs        Number;

		v_Lrbm Vio_Surveil.Lrbm%Type;
		v_Sxbm Vio_Surveil.Sxbm%Type;
		v_Shbm Vio_Surveil.Shbm%Type;
		v_Lrr  Vio_Surveil.Lrr%Type;
		v_Sxr  Vio_Surveil.Sxr%Type;
		v_Shr  Vio_Surveil.Shr%Type;
		v_Lrsj Vio_Surveil.Lrsj%Type;
		v_Sxsj Vio_Surveil.Sxsj%Type;
		v_Shsj Vio_Surveil.Shsj%Type;
		v_Xxly Vio_Surveil.Xxly%Type;
	Begin
		Select Sysdate Into v_S1 From Dual;
		Select Nvl(Max(Tjrq), '20140101') Into v_Laststat From Vio_Stat_Business;
		v_Startdate := To_Date(v_Laststat || '000000', 'yyyymmddhh24miss');
		v_Enddate := Trunc(Sysdate, 'dd');

		If v_Startdate >= v_Enddate Then
			Out_Jg := 1;
			v_Hs := 0;
			Out_Msg := '非现场违法业务信息统计完成，统计至' || To_Char(v_Enddate - 1, 'yyyymmdd') || '。（耗时：' || v_Hs || '秒）';
			Return;
		End If;

		Open c_Yx(v_Startdate, v_Enddate);
		Loop
			Fetch c_Yx
				Into v_Lrbm, v_Lrr, v_Lrsj, v_Xxly, v_Sxbm, v_Sxsj, v_Sxr, v_Shbm, v_Shsj, v_Shr;
			Exit When c_Yx%Notfound;

			v_Tjrq := To_Char(v_Lrsj, 'yyyymmdd');
			If v_Xxly In ('1', '5') Then
				Updateviostatbusi(v_Tjrq, v_Lrbm, '1', '02', '-', v_Lrr);
			Else
				Updateviostatbusi(v_Tjrq, v_Lrbm, '1', '01', '-', v_Lrr);
			End If;

			--v_Tjrq := To_Char(v_Sxsj, 'yyyymmdd');
			Updateviostatbusi(v_Tjrq, v_Sxbm, '2', '01', '-', v_Sxr);

			--v_Tjrq := To_Char(v_Shsj, 'yyyymmdd');
			Updateviostatbusi(v_Tjrq, v_Shbm, '3', '01', '-', v_Shr);
		End Loop;
		Close c_Yx;

		Open c_Wx(v_Startdate, v_Enddate);
		Loop
			Fetch c_Wx
				Into v_Lrbm, v_Lrr, v_Lrsj, v_Xxly, v_Sxbm, v_Sxsj, v_Sxr, v_Shbm, v_Shsj, v_Shr;
			Exit When c_Wx%Notfound;

			v_Tjrq := To_Char(v_Lrsj, 'yyyymmdd');
			If v_Xxly In ('1', '5') Then
				Updateviostatbusi(v_Tjrq, v_Lrbm, '1', '02', '-', v_Lrr);
			Else
				Updateviostatbusi(v_Tjrq, v_Lrbm, '1', '01', '-', v_Lrr);
			End If;

			--v_Tjrq := To_Char(v_Sxsj, 'yyyymmdd');
			Updateviostatbusi(v_Tjrq, v_Sxbm, '2', '02', '-', v_Sxr);

			--v_Tjrq := To_Char(v_Shsj, 'yyyymmdd');
			Updateviostatbusi(v_Tjrq, v_Shbm, '3', '02', '-', v_Shr);
		End Loop;
		Close c_Wx;

		Open c_Sx_Cw(v_Startdate, v_Enddate);
		Loop
			Fetch c_Sx_Cw
				Into v_Lrbm, v_Lrr, v_Lrsj, v_Xxly, v_Sxbm, v_Sxsj, v_Sxr;
			Exit When c_Sx_Cw%Notfound;

			v_Tjrq := To_Char(v_Lrsj, 'yyyymmdd');
			If v_Xxly In ('1', '5') Then
				Updateviostatbusi(v_Tjrq, v_Lrbm, '1', '02', '-', v_Lrr);
			Else
				Updateviostatbusi(v_Tjrq, v_Lrbm, '1', '01', '-', v_Lrr);
			End If;

			--v_Tjrq := To_Char(v_Sxsj, 'yyyymmdd');
			Updateviostatbusi(v_Tjrq, v_Sxbm, '2', '03', '-', v_Sxr);
		End Loop;
		Close c_Sx_Cw;
		Select Mrz Into v_Djglbm From Frm_Syspara Where Gjz = 'DJGLBM';
		Delete From Vio_Stat_Business
		Where Glbm = v_Djglbm And Tjrq = To_Char(v_Enddate, 'yyyymmdd') And Ywlx = '-' And Lx1 = '-' And Lx2 = '-' And
					Czr = '-';
