		Values
			(To_Char(v_Enddate, 'yyyymmdd'), v_Djglbm, '-', '-', '-', 0, '-');
		Commit;
		v_Hs := Trunc((Sysdate - v_S1) * 86400);
		Out_Jg := 1;
		Out_Msg := '非现场违法业务信息统计完成，统计至' || To_Char(v_Enddate - 1, 'yyyymmdd') || '。（耗时：' || v_Hs || '秒）';
	Exception
		When Others Then
			Out_Jg := Sqlcode;
			Out_Msg := '非现场违法业务信息统计失败！' || Sqlerrm;
			Rollback;
	End;

