	Procedure Dovioequipstat
	(
		Out_Jg Out Number, Out_Msg Out Varchar2
	) Is

		Cursor c_Viosurveil
		(
			Kssj Date, Jssj Date
		) Is
			Select Sbbh, Yszt, Lrsj
			From Vio_Surveil_Screen
			Where Sbbh Is Not Null And Lrsj >= Kssj And Lrsj < Jssj
			Union All
			Select Sbbh, Yszt, Lrsj
			From Vio_Surveil_Screen_Err
			Where Sbbh Is Not Null And Lrsj >= Kssj And Lrsj < Jssj
			Union All
			Select Sbbh, Yszt, Lrsj
			From Vio_Surveil_Examine
			Where Sbbh Is Not Null And Lrsj >= Kssj And Lrsj < Jssj
			Union All
			Select Sbbh, Yszt, Lrsj
			From Vio_Surveil_Examine_Err
			Where Sbbh Is Not Null And Lrsj >= Kssj And Lrsj < Jssj
			Union All
			Select Sbbh, Yszt, Lrsj From Vio_Surveil Where Sbbh Is Not Null And Lrsj >= Kssj And Lrsj < Jssj;

		Cursor c_Viosurveil_Err
		(
			Kssj Date, Jssj Date
		) Is
			Select Sbbh, Yszt, Lrsj, Gxsj
			From Vio_Surveil_Screen_Err
			Where Sbbh Is Not Null And Gxsj >= Kssj And Gxsj < Jssj
			Union All
			Select Sbbh, Yszt, Lrsj, Gxsj
			From Vio_Surveil_Examine_Err
			Where Sbbh Is Not Null And Gxsj >= Kssj And Gxsj < Jssj;

		Cursor c_Viosurveil_Time
		(
			Kssj Date, Jssj Date
		) Is
			Select Sbbh, Yszt, Lrsj, Scsj, Gxsj From Vio_Surveil Where Sbbh Is Not Null And Gxsj >= Kssj And Gxsj < Jssj;

		v_Laststat  Varchar2(8);
		v_Startdate Date;
		v_Enddate   Date;

		v_Sbbh Vio_Surveil.Sbbh%Type;
		v_Yszt Vio_Surveil.Yszt%Type;
		v_Lrsj Vio_Surveil.Lrsj%Type;
		v_Scsj Vio_Surveil.Scsj%Type;
		v_Gxsj Vio_Surveil.Gxsj%Type;

		v_Ywlx Vio_Stat_Business.Ywlx%Type;
		v_Lx2  Vio_Stat_Business.Lx2%Type;

		v_Djglbm Varchar2(12);
		v_Tjrq   Varchar2(8);
		v_S1     Date;
		v_Hs     Number;

	Begin

		Select Sysdate Into v_S1 From Dual;
		Select Nvl(Max(Tjrq), '20140101') Into v_Laststat From Vio_Stat_Equipment;

		v_Startdate := To_Date(v_Laststat || '000000', 'yyyymmddhh24miss');
		v_Enddate := Trunc(Sysdate, 'dd');

		If v_Startdate >= v_Enddate Then
			Out_Jg := 1;
			v_Hs := 0;
			Out_Msg := '执法取证设备工作情况统计完成，统计至' || To_Char(v_Enddate - 1, 'yyyymmdd') || '。（耗时：' || v_Hs || '秒）';
			Return;
		End If;

		v_Lx2 := '-';
		Open c_Viosurveil(v_Startdate, v_Enddate);
		Loop
			Fetch c_Viosurveil
				Into v_Sbbh, v_Yszt, v_Lrsj;
			Exit When c_Viosurveil%Notfound;
			v_Tjrq := To_Char(v_Lrsj, 'yyyymmdd');

			If v_Yszt = 1 Then
				v_Ywlx := '2';
				Updateviostatequipment(v_Tjrq, v_Sbbh, v_Ywlx, '02', v_Lx2);
			Elsif v_Yszt = 2 Then
				v_Ywlx := '3';
				Updateviostatequipment(v_Tjrq, v_Sbbh, v_Ywlx, '02', v_Lx2);
			End If;
		End Loop;
		Close c_Viosurveil;

		Open c_Viosurveil_Err(v_Startdate, v_Enddate);
		Loop
			Fetch c_Viosurveil_Err
				Into v_Sbbh, v_Yszt, v_Lrsj, v_Gxsj;
			Exit When c_Viosurveil_Err%Notfound;
			v_Tjrq := To_Char(v_Lrsj, 'yyyymmdd');

			If v_Yszt = 1 Then
				v_Ywlx := '2';
				Updateviostatequipment(v_Tjrq, v_Sbbh, v_Ywlx, '05', v_Lx2);
			Elsif v_Yszt = 2 Then
				v_Ywlx := '3';
				Updateviostatequipment(v_Tjrq, v_Sbbh, v_Ywlx, '05', v_Lx2);
			End If;
		End Loop;
		Close c_Viosurveil_Err;

		Open c_Viosurveil_Time(v_Startdate, v_Enddate);
		Loop
			Fetch c_Viosurveil_Time
				Into v_Sbbh, v_Yszt, v_Lrsj, v_Scsj, v_Gxsj;
			Exit When c_Viosurveil_Time%Notfound;
			v_Tjrq := To_Char(v_Lrsj, 'yyyymmdd');

			If v_Yszt = 1 Then
				v_Ywlx := '2';
				If v_Scsj - v_Lrsj <= 10 Then
					Updateviostatequipment(v_Tjrq, v_Sbbh, v_Ywlx, '03', v_Lx2);
				End If;
				If v_Scsj - v_Lrsj <= 30 Then
					Updateviostatequipment(v_Tjrq, v_Sbbh, v_Ywlx, '04', v_Lx2);
				End If;
			Elsif v_Yszt = 2 Then
				v_Ywlx := '3';
				If v_Scsj - v_Lrsj <= 10 Then
					Updateviostatequipment(v_Tjrq, v_Sbbh, v_Ywlx, '03', v_Lx2);
				End If;
				If v_Scsj - v_Lrsj <= 30 Then
					Updateviostatequipment(v_Tjrq, v_Sbbh, v_Ywlx, '04', v_Lx2);
				End If;
			End If;
		End Loop;
		Close c_Viosurveil_Time;

		Select Mrz Into v_Djglbm From Frm_Syspara Where Gjz = 'DJGLBM';
		Delete From Vio_Stat_Equipment
		Where Sbbh = v_Djglbm And Tjrq = To_Char(v_Enddate, 'yyyymmdd') And Ywlx = '-' And Lx1 = '-' And Lx2 = '-';
		Insert Into Vio_Stat_Equipment
			(Tjrq, Sbbh, Ywlx, Lx1, Lx2, Sl)
		Values
			(To_Char(v_Enddate, 'yyyymmdd'), v_Djglbm, '-', '-', '-', 0);
		Commit;
		v_Hs := Trunc((Sysdate - v_S1) * 86400);
		Out_Jg := 1;
		Out_Msg := '执法取证设备工作情况统计完成，统计至' || To_Char(v_Enddate - 1, 'yyyymmdd') || '。（耗时：' || v_Hs || '秒）';
	Exception
		When Others Then
			Out_Jg := Sqlcode;
			Out_Msg := '执法取证设备工作情况统计失败！' || Sqlerrm;
			Rollback;
	End;

	Procedure Updatevehcheckbusi
	(
		In_Tjrq In Varchar2, In_Glbm In Varchar2, In_Fwzbh In Varchar2, In_Ywlx In Varchar2, In_Lx1 In Varchar2,
		In_Lx2 In Varchar2, In_Lx3 In Varchar2, In_Lx4 In Varchar2, In_Jcr In Varchar2
	) Is
		Yjc Number;
		Djc Number;
		Wjc Number;
	Begin
		Update Stat_Station_Vehicle
		Set Sl = Sl + 1, Csbj = '0', Bjcsbj = '0', Gxsj = Sysdate
		Where Tjrq = In_Tjrq And Glbm = In_Glbm And Fwzbh = In_Fwzbh And Ywlx = In_Ywlx And Lx1 = Nvl(Trim(In_Lx1), '-') And
					Lx2 = Nvl(Trim(In_Lx2), '-') And Sjlb = '1' And Lx3 = Nvl(Trim(In_Lx3), '-') And Lx4 = Nvl(Trim(In_Lx4), '-') And
					Jcr = Nvl(Trim(In_Jcr), '-');
		If Sql%Notfound Then
			Insert Into Stat_Station_Vehicle
				(Tjrq, Glbm, Fwzbh, Ywlx, Lx1, Lx2, Sl, Sjlb, Lx3, Lx4, Jcr)
			Values
				(In_Tjrq, In_Glbm, In_Fwzbh, In_Ywlx, Nvl(Trim(In_Lx1), '-'), Nvl(Trim(In_Lx2), '-'), 1, '1',
				 Nvl(Trim(In_Lx3), '-'), Nvl(Trim(In_Lx4), '-'), Nvl(Trim(In_Jcr), '-'));
		End If;

		-- 用待检查总数-已检查总数 来反求未检查数
		If In_Lx1 = '03' Then
			Select Nvl(Sum(Sl), 0)
			Into Yjc
			From Stat_Station_Vehicle
			Where Tjrq = In_Tjrq And Glbm = In_Glbm And Fwzbh = In_Fwzbh And Ywlx = In_Ywlx And Lx1 = '01' And Sjlb = '1' And
						Lx3 = Nvl(Trim(In_Lx3), '-') And Lx4 = Nvl(Trim(In_Lx4), '-');
			Select Nvl(Sum(Sl), 0)
			Into Djc
			From Stat_Station_Vehicle
			Where Tjrq = In_Tjrq And Glbm = In_Glbm And Fwzbh = In_Fwzbh And Ywlx = In_Ywlx And Lx1 = '03' And Sjlb = '1' And
						Lx3 = Nvl(Trim(In_Lx3), '-') And Lx4 = Nvl(Trim(In_Lx4), '-');

			Wjc := Djc - Yjc;
			If Wjc < 0 Then
				Wjc := 0;
			End If;

			Update Stat_Station_Vehicle
			Set Sl = Wjc, Csbj = '0', Bjcsbj = '0', Gxsj = Sysdate
			Where Tjrq = In_Tjrq And Glbm = In_Glbm And Fwzbh = In_Fwzbh And Ywlx = In_Ywlx And Lx1 = '02' And
						Lx2 = Nvl(Trim(In_Lx2), '-') And Sjlb = '1' And Lx3 = Nvl(Trim(In_Lx3), '-') And
						Lx4 = Nvl(Trim(In_Lx4), '-') And Jcr = '-';
			If Sql%Notfound Then
				Insert Into Stat_Station_Vehicle
					(Tjrq, Glbm, Fwzbh, Ywlx, Lx1, Lx2, Sl, Sjlb, Lx3, Lx4, Jcr)
				Values
					(In_Tjrq, In_Glbm, In_Fwzbh, In_Ywlx, '02', Nvl(Trim(In_Lx2), '-'), 0, '1', Nvl(Trim(In_Lx3), '-'),
					 Nvl(Trim(In_Lx4), '-'), '-');
			End If;
		End If;
	End;

