          If v_Qszt In ('1', '2') Then
            Addvehhandlebusi(v_Tjrq, v_Glbm, v_Fwzbh, 'QSJG', v_Bklx, Nvl(v_Bkzlx, '-'), '-');
          End If;

          -- 确认有效并且出警拦截 应反馈
          \*If v_Qszt = '1' And v_Sfcjlj = '1' Then
            Addvehhandlebusi(v_Tjrq, v_Glbm, v_Fwzbh, 'YFK', '-', '-', '-');
            Addvehhandlebusi(v_Tjrq, v_Glbm, v_Fwzbh, 'YFKBK', v_Bklx, Nvl(v_Bkzlx, '-'), '-');
            --Addstatalarm(v_Glbm, v_Kkbh, v_Tjrq, 'YFK', '-', '-', '');
