            Addvehhandlebusi(v_Tjrq, v_Glbm, v_Fwzbh, 'JSQSBK', v_Bklx, Nvl(v_Bkzlx, '-'), '-');
            --Addstatalarm(v_Glbm, v_Kkbh, v_Tjrq, 'JSQS', '-', '-', '');
          End If;
          --签收人
          Addvehhandlebusi(v_Tjrq, v_Glbm, v_Fwzbh, 'QSR', v_Bklx, Nvl(v_Bkzlx, '-'), v_Czr);
        End If;
      End If;
    End Loop;
    Close c_Alarm_Sure;

    Open c_Alarm_Handle(v_Startdate2, v_Enddate);
    Loop
      Fetch c_Alarm_Handle
        Into v_Glbm, v_Sbzl, v_Sbbh, v_Yjsj, v_Bklx, v_Bkzlx, v_Fkzt, v_Ljzt, v_Fksj, v_Czr, v_Fxlx, v_Ljdw;
      Exit When c_Alarm_Handle%Notfound;
      v_Tjrq := To_Char(v_Yjsj, 'yyyymmdd');
      Select Count(1)
      Into v_Count1
      From Dev_Station_Relation
      Where Sblx = v_Sbzl And Sbbh = v_Sbbh And (Fxlx Like '%' || v_Fxlx || '%' Or Fxlx Is Null) And Zt = '1';
      If v_Count1 > 0 Then
        Select Fwzbh
        Into v_Fwzbh
        From Dev_Station_Relation
        Where Sblx = v_Sbzl And Sbbh = v_Sbbh And (Fxlx Like '%' || v_Fxlx || '%' Or Fxlx Is Null) And Zt = '1';
        Select Count(1) Into v_Count2 From Dev_Station Where Zt = '1' And Fwzbh = v_Fwzbh;
        If v_Count2 > 0 Then
          If v_Czr Is Null Then
            v_Czr := '-';
          End If;
          --Select Glbm Into v_Glbm From Dev_Station Where Zt = '1' And Fwzbh = v_Fwzbh;
          --反馈状态
          If v_Ljdw Is Not Null Then
            Addvehhandlebusi(v_Tjrq, v_Ljdw, v_Fwzbh, 'FKZT', v_Fkzt, '-', '-');
            If v_Fkzt = '1' Then
              Addvehhandlebusi(v_Tjrq, v_Ljdw, v_Fwzbh, 'YIFK', v_Bklx, Nvl(v_Bkzlx, '-'), '-');
            End If;
            --  Addstatalarm(v_Glbm, v_Kkbh, v_Tjrq, 'FKZT', v_Fkzt, '-', '');
            --拦截状态
            Addvehhandlebusi(v_Tjrq, v_Ljdw, v_Fwzbh, 'LJZT', v_Ljzt, '-', '-');
            --Addstatalarm(v_Glbm, v_Kkbh, v_Tjrq, 'LJZT', v_Ljzt, '-', '');
            If v_Ljzt = '1' Then
              Addvehhandlebusi(v_Tjrq, v_Ljdw, v_Fwzbh, 'YICH', v_Bklx, Nvl(v_Bkzlx, '-'), '-');
            End If;
            If v_Ljzt = '0' Then
              Addvehhandlebusi(v_Tjrq, v_Ljdw, v_Fwzbh, 'WCH', v_Bklx, Nvl(v_Bkzlx, '-'), '-');
            End If;
            --及时反馈
            If (v_Fksj - v_Yjsj) <= 1 Then
              Addvehhandlebusi(v_Tjrq, v_Ljdw, v_Fwzbh, 'JSFK', '-', '-', '-');
              Addvehhandlebusi(v_Tjrq, v_Ljdw, v_Fwzbh, 'JSFKBK', v_Bklx, Nvl(v_Bkzlx, '-'), '-');
              --Addstatalarm(v_Glbm, v_Kkbh, v_Tjrq, 'JSFK', '-', '-', '');
            End If;

            --拦截民警
            Addvehhandlebusi(v_Tjrq, v_Ljdw, v_Fwzbh, 'LJMJ', v_Bklx, Nvl(v_Bkzlx, '-'), v_Czr);
          End If;
        End If;
      End If;
    End Loop;
    Close c_Alarm_Handle;

    Select Mrz Into v_Djglbm From Frm_Syspara Where Gjz = 'DJGLBM';

    v_Hs := Trunc((Sysdate - v_S1) * 86400);
    Insert Into Stat_Station_Alarm
      (Tjrq, Glbm, Fwzbh, Fl, Lx1, Lx2, Czr, Sl)
    Values
      (To_Char(v_Enddate, 'yyyymmdd'), v_Djglbm, '-', '-', '-', 'RKSJ', '-', v_Hs);
    Commit;*/
		v_Hs := Trunc((Sysdate - v_S1) * 86400);
		Out_Jg := 1;
		Out_Msg := '操作成功，统计至' || To_Char(v_Enddate - 1, 'yyyymmdd') || '，耗时（秒）：' || v_Hs;
	Exception
		When Others Then
			Out_Jg := Sqlcode;
			Out_Msg := '操作失败！' || Sqlerrm;
			Rollback;
	End;

	Procedure Dovehcheckbusi
	(
		Out_Jg Out Number, Out_Msg Out Varchar2
	) Is
		Cursor c_Vehchecked
		(
			Kssj Date, Jssj Date
		) Is
			Select Jcxh, Fwzbh, Jcbj, Jccllx, Jcmj, Jcsj, Clzt, Syxz, Cllx, Jcjg, Clyqwbf, Clyqwjy, Clwfwcl, Sfwzjs, Gps, Aqsb,
						 Cllthw, Jaqd, Pljs, Ztfgbs, Azfhzz, Ffgz, Cz, Wfyy, Cy, Xgjsbz, Azdsj, Azdlx, Qdystxz
			From Serv_Veh_Check
			Where Jcbj = '1' And Cjsj >= Kssj And Cjsj < Jssj And Jcsj Is Not Null;

		Cursor c_Vehcheck
		(
			Kssj Date, Jssj Date
		) Is
			Select Fwzbh, Jcbj, Jccllx, Jcmj, Rksj, Cllx, Syxz From Serv_Veh_Check Where Rksj >= Kssj And Rksj < Jssj;

		v_Laststat Varchar2(8);

		v_Startdate Date;

		v_Enddate Date;
		v_S1      Date;
		v_Hs      Number;
		v_Djglbm  Varchar2(12);

		v_Tjrq    Stat_Station_Vehicle.Tjrq%Type;
		v_Jcxh    Serv_Veh_Check.Jcxh%Type;
		v_Fwzbh   Stat_Station_Vehicle.Fwzbh%Type;
		v_Jcbj    Serv_Veh_Check.Jcbj%Type;
		v_Jccllx  Serv_Veh_Check.Jccllx%Type;
		v_Jcmj    Serv_Veh_Check.Jcmj%Type;
		v_Jcsj    Serv_Veh_Check.Jcsj%Type;
		v_Glbm    Dev_Station.Glbm%Type;
		v_Rksj    Serv_Veh_Check.Rksj%Type;
		v_Clzt    Serv_Veh_Check.Clzt%Type;
		v_Syxz    Serv_Veh_Check.Syxz%Type;
		v_Cllx    Serv_Veh_Check.Cllx%Type;
		v_Jcjg    Serv_Veh_Check.Jcjg%Type;
		v_Clyqwbf Serv_Veh_Check.Clyqwbf%Type;
		v_Clyqwjy Serv_Veh_Check.Clyqwjy%Type;
		v_Clwfwcl Serv_Veh_Check.Clwfwcl%Type;
		v_Sfwzjs  Serv_Veh_Check.Sfwzjs%Type;
		v_Sfcf    Serv_Veh_Check_Driver.Sfcf%Type;
		v_Sfzjbf  Serv_Veh_Check_Driver.Sfzjbf%Type;
		v_Sfyqwhz Serv_Veh_Check_Driver.Sfyqwhz%Type;
		v_Sfyqwsy Serv_Veh_Check_Driver.Sfyqwsy%Type;
		v_Gps     Serv_Veh_Check.Gps%Type;
		v_Aqsb    Serv_Veh_Check.Aqsb%Type;
		v_Cllthw  Serv_Veh_Check.Cllthw%Type;
		v_Jaqd    Serv_Veh_Check.Jaqd%Type;
		v_Pljs    Serv_Veh_Check.Pljs%Type;
		v_Ztfgbs  Serv_Veh_Check.Ztfgbs%Type;
		v_Azfhzz  Serv_Veh_Check.Azfhzz%Type;
		v_Ffgz    Serv_Veh_Check.Ffgz%Type;
		v_Cz      Serv_Veh_Check.Cz%Type;
		v_Wfyy    Serv_Veh_Check.Wfyy%Type;
		v_Cy      Serv_Veh_Check.Cy%Type;
		v_Xgjsbz  Serv_Veh_Check.Xgjsbz%Type;
		v_Azdsj   Serv_Veh_Check.Azdsj%Type;
		v_Azdlx   Serv_Veh_Check.Azdlx%Type;
		v_Qdystxz Serv_Veh_Check.Qdystxz%Type;

		Icount Number;

		v_Clyc  Number;
		v_Jsyyc Number;

	Begin
		Select Sysdate Into v_S1 From Dual;
		Select Nvl(Max(Tjrq), '20140101') Into v_Laststat From Stat_Station_Vehicle Where Sjlb = '1';

		v_Startdate := To_Date(v_Laststat || '000000', 'yyyymmddhh24miss');
		v_Enddate := Trunc(Sysdate, 'dd');

		If v_Startdate >= v_Enddate Then
			Out_Jg := 1;
			v_Hs := 0;
			Out_Msg := '交警执法站台账统计完成，统计至' || To_Char(v_Enddate - 1, 'yyyymmdd') || '。（耗时：' || v_Hs || '秒）';
			--Out_Msg := '1212121';
			Return;
		End If;

		Open c_Vehchecked(v_Startdate, v_Enddate);
		Loop
			Fetch c_Vehchecked
				Into v_Jcxh, v_Fwzbh, v_Jcbj, v_Jccllx, v_Jcmj, v_Jcsj, v_Clzt, v_Syxz, v_Cllx, v_Jcjg, v_Clyqwbf, v_Clyqwjy,
						 v_Clwfwcl, v_Sfwzjs, v_Gps, v_Aqsb, v_Cllthw, v_Jaqd, v_Pljs, v_Ztfgbs, v_Azfhzz, v_Ffgz, v_Cz, v_Wfyy,
						 v_Cy, v_Xgjsbz, v_Azdsj, v_Azdlx, v_Qdystxz;
			Exit When c_Vehchecked%Notfound;
			v_Tjrq := To_Char(v_Jcsj, 'yyyymmdd');

			Select Count(1) Into Icount From Dev_Station Where Fwzbh = v_Fwzbh;
			If Icount > 0 Then
				Select Glbm Into v_Glbm From Dev_Station Where Fwzbh = v_Fwzbh And Rownum = 1;
				Updatevehcheckbusi(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '01', v_Jcmj, Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), v_Jcmj);

				-- 检查车辆数
				Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '01', '-', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');

				v_Clyc := 0;
				v_Jsyyc := 0;

				-- 车辆异常:作案车辆
				If v_Jcjg In ('10', '99') Then
					If v_Clyc = 0 Then
						v_Clyc := 1;
						Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '13', '01', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
					End If;
					Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '03', '01', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
				End If;

				-- 车辆异常:无牌
				If v_Clzt = '2' Then
					If v_Clyc = 0 Then
						v_Clyc := 1;
						Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '13', '02', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
					End If;
					Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '03', '02', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
				End If;

				-- 车辆异常:假牌
				If v_Clzt = '4' Then
					If v_Clyc = 0 Then
						v_Clyc := 1;
						Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '13', '03', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
					End If;
					Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '03', '03', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
				End If;

				-- 车辆异常:套牌
				If v_Clzt = '3' Then
					If v_Clyc = 0 Then
						v_Clyc := 1;
						Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '13', '04', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
					End If;
					Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '03', '04', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
				End If;

				-- 车辆异常:逾期未报废
				If v_Clyqwbf = '1' Then
					If v_Clyc = 0 Then
						v_Clyc := 1;
						Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '13', '05', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
					End If;
					Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '03', '05', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
				End If;

				-- 车辆异常:逾期未检验
				If v_Clyqwjy = '1' Then
					If v_Clyc = 0 Then
						v_Clyc := 1;
						Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '13', '06', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
					End If;
					Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '03', '06', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
				End If;

				-- 车辆异常:违法记录未处理
				If v_Clwfwcl = '1' Then
					If v_Clyc = 0 Then
						v_Clyc := 1;
						Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '13', '07', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
					End If;
					Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '03', '07', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
				End If;

				-- 正常数
				If v_Clyc = 0 Then
					Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '02', '-', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
				End If;

				-- 驾驶员异常:犯罪嫌疑人
				If v_Jcjg In ('20', '99') Then
					If v_Jsyyc = 0 Then
						v_Jsyyc := 1;
						Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '14', '01', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
					End If;
					Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '04', '01', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
				End If;

				-- 驾驶员异常:无证驾驶
				If v_Sfwzjs = '1' Then
					If v_Jsyyc = 0 Then
						v_Jsyyc := 1;
						Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '14', '02', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
					End If;
					Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '04', '02', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
				End If;

				Select Count(1) Into Icount From Serv_Veh_Check_Driver Where Jcxh = v_Jcxh;
				If Icount > 0 Then
					-- 驾驶员异常:准驾不符
					Select Sfzjbf
					Into v_Sfzjbf
					From (Select * From Serv_Veh_Check_Driver Where Jcxh = v_Jcxh Order By Sfzjbf Desc)
					Where Rownum = 1;
					If v_Sfzjbf = '1' Then
						If v_Jsyyc = 0 Then
							v_Jsyyc := 1;
							Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '14', '03', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
						End If;
						Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '04', '03', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
					End If;

					-- 驾驶员异常:超12分
					Select Sfcf
					Into v_Sfcf
					From (Select * From Serv_Veh_Check_Driver Where Jcxh = v_Jcxh Order By Sfcf Desc)
					Where Rownum = 1;
					If v_Sfcf = '1' Then
						If v_Jsyyc = 0 Then
							v_Jsyyc := 1;
							Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '14', '04', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
						End If;
						Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '04', '04', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
					End If;

					-- 驾驶员异常:逾期未审验
					Select Sfyqwsy
					Into v_Sfyqwsy
					From (Select * From Serv_Veh_Check_Driver Where Jcxh = v_Jcxh Order By Sfyqwsy Desc)
					Where Rownum = 1;
					If v_Sfyqwsy = '1' Then
						If v_Jsyyc = 0 Then
							v_Jsyyc := 1;
							Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '14', '05', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
						End If;
						Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '04', '05', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
					End If;

					-- 驾驶员异常:逾期未换证
					Select Sfyqwhz
					Into v_Sfyqwhz
					From (Select * From Serv_Veh_Check_Driver Where Jcxh = v_Jcxh Order By Sfyqwhz Desc)
					Where Rownum = 1;
					If v_Sfyqwhz = '1' Then
						If v_Jsyyc = 0 Then
							v_Jsyyc := 1;
							Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '14', '06', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
						End If;
						Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '04', '06', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
					End If;
				End If;

				-- 检查内容异常:GPS不正常
				If v_Gps = '0' Then
					Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '05', '01', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
				End If;

				-- 检查内容异常:GPS未配备
				If v_Gps = '9' Then
					Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '05', '02', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
				End If;

				-- 检查内容异常:安全设备配备不合格
				If v_Aqsb = '0' Then
					Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '05', '03', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
				End If;

				-- 检查内容异常:车辆轮胎花纹不合格
				If v_Cllthw = '0' Then
					Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '05', '04', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
				End If;

				-- 检查内容异常:未系安全带
				If v_Jaqd = '0' Then
					Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '05', '05', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
				End If;

				-- 检查内容异常:疲劳驾驶
				If v_Pljs = '1' Then
					Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '05', '06', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
				End If;

				-- 大货车
				If v_Jccllx = '04' Then
					-- 大货车:检查内容异常:未按规定粘贴反光标识
					If v_Ztfgbs = '0' Then
						Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '05', '07', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
					End If;

					-- 大货车:检查内容异常:未安装侧后防护装置
					If v_Azfhzz = '0' Then
						Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '05', '08', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
					End If;

					-- 大货车:检查内容异常:非法改装
					If v_Ffgz = '1' Then
						Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '05', '09', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
					End If;

					-- 大货车:检查内容异常:超载
					If v_Cz = '1' Then
						Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '05', '10', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
					End If;

				End If;

				-- 大客车/校车
				If v_Jccllx In ('01', '03') Then
					-- 大客车/校车:检查内容异常:违法营运
					If v_Wfyy = '1' Then
						Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '05', '07', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
					End If;

					-- 大客车/校车:检查内容异常:超员
					If v_Cy = '1' Then
						Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '05', '08', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
					End If;
				End If;

				-- 危化品运输车
				If v_Jccllx = '02' Then
					-- 危化品运输车:检查内容异常:未悬挂或喷涂警示标志
					If v_Xgjsbz = '0' Then
						Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '05', '07', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
					End If;

					-- 危化品运输车:检查内容异常:未按指定时间行驶
					If v_Azdsj = '0' Then
						Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '05', '08', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
					End If;

					-- 危化品运输车:检查内容异常:未按指定路线行驶
					If v_Azdlx = '0' Then
						Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '05', '09', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
					End If;

					-- 危化品运输车:检查内容异常:未取得道路运输通行证
					If v_Qdystxz = '0' Then
						Updatevehcheck(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '05', '10', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
					End If;
				End If;

			End If;
		End Loop;
		Close c_Vehchecked;

		Open c_Vehcheck(v_Startdate, v_Enddate);
		Loop
			Fetch c_Vehcheck
				Into v_Fwzbh, v_Jcbj, v_Jccllx, v_Jcmj, v_Rksj, v_Cllx, v_Syxz;
			Exit When c_Vehcheck%Notfound;
			v_Tjrq := To_Char(v_Rksj, 'yyyymmdd');

			Select Count(1) Into Icount From Dev_Station Where Fwzbh = v_Fwzbh;
			If Icount > 0 Then
				Select Glbm Into v_Glbm From Dev_Station Where Fwzbh = v_Fwzbh And Rownum = 1;
				Updatevehcheckbusi(v_Tjrq, v_Glbm, v_Fwzbh, v_Jccllx, '03', '-', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'), '-');
			End If;
		End Loop;
		Close c_Vehcheck;

		Select Mrz Into v_Djglbm From Frm_Syspara Where Gjz = 'DJGLBM';
		Delete From Stat_Station_Vehicle
		Where Glbm = v_Djglbm And Fwzbh = '-' And Tjrq = To_Char(v_Enddate, 'yyyymmdd') And Ywlx = '-' And Lx1 = '-' And
					Lx2 = '-' And Sjlb = '1' And Lx3 = '-' And Lx4 = '-' And Jcr = '-';
		Insert Into Stat_Station_Vehicle
			(Tjrq, Glbm, Fwzbh, Ywlx, Lx1, Lx2, Sl, Sjlb, Lx3, Lx4, Jcr)
		Values
			(To_Char(v_Enddate, 'yyyymmdd'), v_Djglbm, '-', '-', '-', '-', 0, '1', '-', '-', '-');

		Delete From Stat_Station_Vehicle
		Where Glbm = v_Djglbm And Fwzbh = '-' And Tjrq = To_Char(v_Enddate, 'yyyymmdd') And Ywlx = '-' And Lx1 = '-' And
					Lx2 = '-' And Lx3 = '-' And Lx4 = '-' And Sjlb = '2' And Jcr = '-';
		Insert Into Stat_Station_Vehicle
			(Tjrq, Glbm, Fwzbh, Ywlx, Lx1, Lx2, Lx3, Lx4, Sl, Sjlb, Jcr)
		Values
			(To_Char(v_Enddate, 'yyyymmdd'), v_Djglbm, '-', '-', '-', '-', '-', '-', 0, '2', '-');

		v_Hs := Trunc((Sysdate - v_S1) * 86400);
		Commit;
		Out_Jg := 1;
		Out_Msg := '交警执法站台账统计完成，统计至' || To_Char(v_Enddate - 1, 'yyyymmdd') || '。（耗时：' || v_Hs || '秒）';
	Exception
		When Others Then
			Out_Jg := Sqlcode;
			Out_Msg := '交警执法站台账统计失败！' || Sqlerrm;
			Rollback;
	End;

	Procedure Updateservflow
	(
		In_Tjrq In Varchar2, In_Glbm In Varchar2, In_Fwzbh In Varchar2, In_Ywlx In Varchar2, In_Lx1 In Varchar2,
		In_Lx2 In Varchar2, In_Lx3 In Varchar2, In_Lx4 In Varchar2
	) Is
	Begin
		Update Serv_Stat_Flow
		Set Sl = Sl + 1, Csbj = '0', Bjcsbj = '0', Gxsj = Sysdate
		Where Tjrq = In_Tjrq And Glbm = In_Glbm And Fwzbh = In_Fwzbh And Ywlx = In_Ywlx And Lx1 = Nvl(Trim(In_Lx1), '-') And
					Lx2 = Nvl(Trim(In_Lx2), '-') And Lx3 = Nvl(Trim(In_Lx3), '-') And Lx4 = Nvl(Trim(In_Lx4), '-');
		If Sql%Notfound Then
			Insert Into Serv_Stat_Flow
				(Tjrq, Glbm, Fwzbh, Ywlx, Lx1, Lx2, Lx3, Lx4, Sl)
			Values
				(In_Tjrq, In_Glbm, In_Fwzbh, In_Ywlx, Nvl(Trim(In_Lx1), '-'), Nvl(Trim(In_Lx2), '-'), Nvl(Trim(In_Lx3), '-'),
				 Nvl(Trim(In_Lx4), '-'), 1);
		End If;
	End;

	Procedure Dovehcheckflow
	(
		Out_Jg Out Number, Out_Msg Out Varchar2
	) Is

		Cursor c_Servflow
		(
			Kssj Date, Jssj Date
		) Is
			Select Fwzbh, Ywlx, Syxz, Cllx, Yxqsbj, Yxshbj, Nwrksj
			From Serv_Station_Flow
			Where Nwrksj >= Kssj And Nwrksj < Jssj;

		Cursor c_Servflow_Qs
		(
			Kssj Date, Jssj Date
		) Is
			Select Fwzbh, Ywlx, Syxz, Cllx, Qsbj, Nwrksj
			From Serv_Station_Flow
			Where Qssj Is Not Null And Qssj >= Kssj And Qssj < Jssj;

		Cursor c_Servflow_Sh
		(
			Kssj Date, Jssj Date
		) Is
			Select Fwzbh, Ywlx, Syxz, Cllx, Shbj, Nwrksj
			From Serv_Station_Flow
			Where Shsj Is Not Null And Shsj >= Kssj And Shsj < Jssj;

		v_Laststat  Varchar2(8);
		v_Startdate Date;
		v_Enddate   Date;

		v_Glbm   Serv_Stat_Flow.Glbm%Type;
		v_Fwzbh  Serv_Station_Flow.Fwzbh%Type;
		v_Ywlx   Serv_Station_Flow.Ywlx%Type;
		v_Syxz   Serv_Station_Flow.Syxz%Type;
		v_Cllx   Serv_Station_Flow.Cllx%Type;
		v_Yxqsbj Serv_Station_Flow.Yxqsbj%Type;
		v_Yxshbj Serv_Station_Flow.Yxshbj%Type;
		v_Nwrksj Serv_Station_Flow.Nwrksj%Type;
		v_Qsbj   Serv_Station_Flow.Qsbj%Type;
		v_Shbj   Serv_Station_Flow.Shbj%Type;

		v_Djglbm Varchar2(12);
		v_Tjrq   Varchar2(8);
		v_S1     Date;
		v_Hs     Number;
		Icount   Number;

	Begin

		Select Sysdate Into v_S1 From Dual;
		Select Nvl(Max(Tjrq), '20140101') Into v_Laststat From Serv_Stat_Flow;

		v_Startdate := To_Date(v_Laststat || '000000', 'yyyymmddhh24miss');
		v_Enddate := Trunc(Sysdate, 'dd');

		If v_Startdate >= v_Enddate Then
			Out_Jg := 1;
			v_Hs := 0;
			Out_Msg := '执法站业务流水信息统计完成，统计至' || To_Char(v_Enddate - 1, 'yyyymmdd') || '。（耗时：' || v_Hs || '秒）';
			Return;
		End If;

		Open c_Servflow(v_Startdate, v_Enddate);
		Loop
			Fetch c_Servflow
				Into v_Fwzbh, v_Ywlx, v_Syxz, v_Cllx, v_Yxqsbj, v_Yxshbj, v_Nwrksj;
			Exit When c_Servflow%Notfound;
			v_Tjrq := To_Char(v_Nwrksj, 'yyyymmdd');

			Select Count(1) Into Icount From Dev_Station Where Fwzbh = v_Fwzbh;
			If Icount > 0 Then
				Select Glbm Into v_Glbm From Dev_Station Where Fwzbh = v_Fwzbh And Rownum = 1;

				If v_Yxqsbj = '1' Then
					-- 需要签收的数量
					Updateservflow(v_Tjrq, v_Glbm, v_Fwzbh, v_Ywlx, '01', '-', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'));
				End If;

				If v_Yxshbj = '1' Then
					-- 需要审核的数量
					Updateservflow(v_Tjrq, v_Glbm, v_Fwzbh, v_Ywlx, '02', '-', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'));
				End If;
			End If;

		End Loop;
		Close c_Servflow;

		Open c_Servflow_Qs(v_Startdate, v_Enddate);
		Loop
			Fetch c_Servflow_Qs
				Into v_Fwzbh, v_Ywlx, v_Syxz, v_Cllx, v_Qsbj, v_Nwrksj;
			Exit When c_Servflow_Qs%Notfound;
			v_Tjrq := To_Char(v_Nwrksj, 'yyyymmdd');

			Select Count(1) Into Icount From Dev_Station Where Fwzbh = v_Fwzbh;
			If Icount > 0 Then
				Select Glbm Into v_Glbm From Dev_Station Where Fwzbh = v_Fwzbh And Rownum = 1;

				If v_Qsbj = '1' Then
					-- 已签收
					Updateservflow(v_Tjrq, v_Glbm, v_Fwzbh, v_Ywlx, '03', '-', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'));
				End If;
			End If;

		End Loop;
		Close c_Servflow_Qs;

		Open c_Servflow_Sh(v_Startdate, v_Enddate);
		Loop
			Fetch c_Servflow_Sh
				Into v_Fwzbh, v_Ywlx, v_Syxz, v_Cllx, v_Shbj, v_Nwrksj;
			Exit When c_Servflow_Sh%Notfound;
			v_Tjrq := To_Char(v_Nwrksj, 'yyyymmdd');

			Select Count(1) Into Icount From Dev_Station Where Fwzbh = v_Fwzbh;
			If Icount > 0 Then
				Select Glbm Into v_Glbm From Dev_Station Where Fwzbh = v_Fwzbh And Rownum = 1;

				If v_Shbj = '1' Then
					-- 审核收
					Updateservflow(v_Tjrq, v_Glbm, v_Fwzbh, v_Ywlx, '04', '-', Nvl(v_Syxz, '-'), Nvl(v_Cllx, '-'));
				End If;
			End If;

		End Loop;
		Close c_Servflow_Sh;

		Select Mrz Into v_Djglbm From Frm_Syspara Where Gjz = 'DJGLBM';
		Delete From Serv_Stat_Flow
		Where Glbm = v_Djglbm And Tjrq = To_Char(v_Enddate, 'yyyymmdd') And Fwzbh = '-' And Ywlx = '-' And Lx1 = '-' And
					Lx2 = '-' And Lx3 = '-' And Lx4 = '-';
		Insert Into Serv_Stat_Flow
			(Tjrq, Glbm, Fwzbh, Ywlx, Lx1, Lx2, Lx3, Lx4, Sl)
		Values
			(To_Char(v_Enddate, 'yyyymmdd'), v_Djglbm, '-', '-', '-', '-', '-', '-', 0);
		Commit;
		v_Hs := Trunc((Sysdate - v_S1) * 86400);
		Out_Jg := 1;
		Out_Msg := '执法站业务流水信息统计完成，统计至' || To_Char(v_Enddate - 1, 'yyyymmdd') || '。（耗时：' || v_Hs || '秒）';
	Exception
		When Others Then
			Out_Jg := Sqlcode;
			Out_Msg := '执法站业务流水信息统计失败！' || Sqlerrm;
			Rollback;
	End;

