	Procedure Dostatpasshour
	(
		Out_Jg Out Number, Out_Msg Out Varchar2
	) Is
		v_Gxsj      Varchar2(2000);
		v_Date      Varchar2(2000);
		v_Starttime Date;
		v_Hs        Number;
		i_Count     Number;
	Begin
		Select Sysdate Into v_Starttime From Dual;

		Select Count(1) Into i_Count From Rm_Args Where Gjz = 'STAT_PASS_HOUR_GXSJ';

		If i_Count = 0 Then
			Insert Into Rm_Args (Gjz, Csz) Values ('STAT_PASS_HOUR_GXSJ', To_Char(Sysdate - 1, 'yyyy-mm-dd hh24:mi:ss'));
			Commit;
			Out_Jg := -1;
			Out_Msg := '未找到小时流量入队列表统计锁';
			Return;
		End If;
