	Procedure Dostatpassdayflow
	(
		In_Kssj In Varchar2, In_Jssj In Varchar2, Out_Jg Out Number, Out_Msg Out Varchar2
	) Is
		Cursor c_Dayflow Is
			Select Kkbh, Fxlx, Substr(Tjsd, 1, 8) Tjrq, ' ' Pzsd, Sum(Xss) Xss, Max(Glbm) Glbm, Max(Xzqh) Xzqh, Max(Dldm) Dldm,
						 Max(Lddm) Lddm, Max(Jd) Jd, Max(Wd) Wd, Max(Bhll) Bhll, Sum(Txclzs) Txclzs, Max(Bhd) Bhd, Sum(Txdcs) Txdcs,
						 Sum(Txxcs) Txxcs, Sum(Txbscs) Txbscs, Sum(Txwscs) Txwscs,
						 Round(Sum(Dcpjsd * Sdyxdcs + Xcpjsd * Sdyxxcs) /
										Decode(Sum(Sdyxdcs + Sdyxxcs), 0, 1, Sum(Sdyxdcs + Sdyxxcs)), 1) Pjsd,
						 Round(Sum(Dcpjsd * Sdyxdcs) / Decode(Sum(Sdyxdcs), 0, 1, Sum(Sdyxdcs)), 1) Dcpjsd, Sum(Sdyxdcs) Sdyxdcs,
						 Round(Sum(Xcpjsd * Sdyxxcs) / Decode(Sum(Sdyxxcs), 0, 1, Sum(Sdyxxcs)), 1) Xcpjsd, Sum(Sdyxxcs) Sdyxxcs,
						 Sysdate Gxsj, '' Bz
			From (Select t.*,
										Case
											When Substr(Tjsd, 9, 2) >= (Select Frm_Func_Pkg.Getsplitstr(Csz, '-', 1)
																									From Frm_Ctrlpara
																									Where Gjz = 'PZSD' And Rownum = 1) And
													 Substr(Tjsd, 9, 2) <= (Select Frm_Func_Pkg.Getsplitstr(Csz, '-', 2)
																									From Frm_Ctrlpara
																									Where Gjz = 'PZSD' And Rownum = 1) Then
											 1
											Else
											 0
										End Xss
						 From Stat_Pass_Hour t
						 Where Gxsj >= To_Date(In_Kssj, 'yyyy-mm-dd hh24:mi:ss') And Gxsj < To_Date(In_Jssj, 'yyyy-mm-dd hh24:mi:ss'))
			Group By Kkbh, Fxlx, Substr(Tjsd, 1, 8)
			Order By Kkbh, Fxlx, Tjrq;
		Dayflow_Obj Stat_Pass_Day%Rowtype;
		v_Pzsd      Varchar2(32);
		v_Starttime Date;
		v_Hs        Number;
		i_Count     Number;
	Begin
		Select Sysdate Into v_Starttime From Dual;
		Select Count(1) Into i_Count From Rm_Args Where Gjz = 'STAT_PASS_DAY_FLOW';
		Select Csz Into v_Pzsd From Frm_Ctrlpara Where Gjz = 'PZSD' And Rownum = 1;
		If i_Count = 0 Then
			Insert Into Rm_Args
				(Gjz, Csz)
			Values
				('STAT_PASS_DAY_FLOW', To_Char('2015-01-01 00:00:00', 'yyyy-mm-dd hh24:mi:ss'));
			Commit;
			Out_Jg := -1;
			Out_Msg := '未找到日流量统计锁';
			Return;
		End If;
		Open c_Dayflow;
		Loop
			Fetch c_Dayflow
				Into Dayflow_Obj;
			Exit When c_Dayflow%Notfound;
			Select Count(1)
			Into i_Count
			From Stat_Pass_Day
			Where Kkbh = Dayflow_Obj.Kkbh And Fxlx = Dayflow_Obj.Fxlx And Tjrq = Dayflow_Obj.Tjrq;
			If i_Count = 0 Then
				Insert Into Stat_Pass_Day
					(Kkbh, Fxlx, Tjrq, Pzsd, Xss, Glbm, Xzqh, Dldm, Lddm, Jd, Wd, Bhll, Txclzs, Bhd, Txdcs, Txxcs, Txbscs, Txwscs,
					 Pjsd, Dcpjsd, Sdyxdcs, Xcpjsd, Sdyxxcs, Gxsj, Bz)
				Values
					(Dayflow_Obj.Kkbh, Dayflow_Obj.Fxlx, Dayflow_Obj.Tjrq, v_Pzsd, Dayflow_Obj.Xss, Dayflow_Obj.Glbm,
					 Dayflow_Obj.Xzqh, Dayflow_Obj.Dldm, Dayflow_Obj.Lddm, Dayflow_Obj.Jd, Dayflow_Obj.Wd, Dayflow_Obj.Bhll,
					 Dayflow_Obj.Txclzs, Dayflow_Obj.Bhd, Dayflow_Obj.Txdcs, Dayflow_Obj.Txxcs, Dayflow_Obj.Txbscs,
					 Dayflow_Obj.Txwscs, Dayflow_Obj.Pjsd, Dayflow_Obj.Dcpjsd, Dayflow_Obj.Sdyxdcs, Dayflow_Obj.Xcpjsd,
					 Dayflow_Obj.Sdyxxcs, Sysdate, '');
			Else
				Delete From Stat_Pass_Day
				Where Kkbh = Dayflow_Obj.Kkbh And Fxlx = Dayflow_Obj.Fxlx And Tjrq = Dayflow_Obj.Tjrq;
				Insert Into Stat_Pass_Day
					Select Kkbh, Fxlx, Substr(Tjsd, 1, 8) Tjrq, v_Pzsd, Sum(Xss) Xss, Max(Glbm) Glbm, Max(Xzqh) Xzqh,
								 Max(Dldm) Dldm, Max(Lddm) Lddm, Max(Jd) Jd, Max(Wd) Wd, Sum(Bhll) Bhll, Sum(Txclzs) Txclzs,
								 Max(Bhd) Bhd, Sum(Txdcs) Txdcs, Sum(Txxcs) Txxcs, Sum(Txbscs) Txbscs, Sum(Txwscs) Txwscs,
								 Round(Sum(Dcpjsd * Sdyxdcs + Xcpjsd * Sdyxxcs) /
												Decode(Sum(Sdyxdcs + Sdyxxcs), 0, 1, Sum(Sdyxdcs + Sdyxxcs)), 1) Pjsd,
								 Round(Sum(Dcpjsd * Sdyxdcs) / Decode(Sum(Sdyxdcs), 0, 1, Sum(Sdyxdcs)), 1) Dcpjsd, Sum(Sdyxdcs) Sdyxdcs,
								 Round(Sum(Xcpjsd * Sdyxxcs) / Decode(Sum(Sdyxxcs), 0, 1, Sum(Sdyxxcs)), 1) Xcpjsd, Sum(Sdyxxcs) Sdyxxcs,
								 Sysdate, ''
					From (Select t.*,
												Case
													When Substr(Tjsd, 9, 2) >= (Select Frm_Func_Pkg.Getsplitstr(Csz, '-', 1)
																											From Frm_Ctrlpara
																											Where Gjz = 'PZSD' And Rownum = 1) And
															 Substr(Tjsd, 9, 2) <= (Select Frm_Func_Pkg.Getsplitstr(Csz, '-', 2)
																											From Frm_Ctrlpara
																											Where Gjz = 'PZSD' And Rownum = 1) Then
													 1
													Else
													 0
												End Xss
								 From Stat_Pass_Hour t
								 Where Kkbh = Dayflow_Obj.Kkbh And Fxlx = Dayflow_Obj.Fxlx And Substr(Tjsd, 1, 8) = Dayflow_Obj.Tjrq)
					Group By Kkbh, Fxlx, Substr(Tjsd, 1, 8);
			End If;
			Frm_Comm_Pkg.Writeupqueue(Null, '6396', Dayflow_Obj.Kkbh || '#' || Dayflow_Obj.Fxlx || '#' || Dayflow_Obj.Tjrq);
			Frm_Comm_Pkg.Writeupqueue(Null, '63X6', Dayflow_Obj.Kkbh || '#' || Dayflow_Obj.Fxlx || '#' || Dayflow_Obj.Tjrq);
		End Loop;
		Close c_Dayflow;
		v_Hs := Trunc((Sysdate - v_Starttime) * 86400);
		Update Rm_Args Set Csz = In_Jssj Where Gjz = 'STAT_PASS_DAY_FLOW';
		Commit;
		Out_Jg := 1;
		Out_Msg := '过车日流量统计完成，最后获取的更新时间为:' || In_Jssj || '。（耗时：' || v_Hs || '秒）';
	End;

End Rm_Stat_Pkg;
