    Dept_Obj Frm_Department%Rowtype;
    Icount   Number;
  Begin
    Select * Into Dept_Obj From Frm_Department Where Glbm = Fwbm;

    -- 已经存在的同级部门数量
    Select Count(1)
    Into Icount
    From Dev_Vgat_Posicam_Control a, Frm_Department b
    Where a.Fwbm = b.Glbm And b.Sjbm = Dept_Obj.Sjbm;
    If Dwlx = '2' Then
      -- 如果访问部门是大队
      If Dept_Obj.Bmjb = '4' Then
        If Icount >= 3 Then
          Return '省内视频最多只能授权给3个大队';
