  --获取一个点位的状态
  Function Getstatus(In_Dwxh Varchar2) Return Number Is
    Icount Number;
    Icount2 Number;
    v_dwzt   Dev_Vgat_Position.Dwzt%Type;
    v_dwzt1   Dev_Vgat_Position_Temp.Dwzt%Type;
    v_shbj   Dev_Vgat_Position_Temp.Shbj%Type;
  Begin
    Select Count(1) Into Icount From Dev_Vgat_Position Where Dwxh = In_Dwxh;
    Select Count(1) Into Icount2 From Dev_Vgat_Position_Temp Where Dwxh = In_Dwxh;
    If Icount = 0 Then --尚未备案成功
       If Icount2 = 1 Then
         Select Shbj Into v_shbj From Dev_Vgat_Position_Temp Where Dwxh = In_Dwxh;
         If v_shbj = '0' Then --备案待审核
            Return 0;
         ElsIf v_shbj = '2' Then  --备案审核不通过
            Return 10;
         Else
            Return -1;
         End If;
       Else
         Return -1;
       End If;
    Else               --已经备案过
       If Icount2 = 1 Then
          Select Dwzt Into v_dwzt From Dev_Vgat_Position Where Dwxh = In_Dwxh;
          Select Dwzt Into v_dwzt1 From Dev_Vgat_Position_Temp Where Dwxh = In_Dwxh;
          Select Shbj Into v_shbj From Dev_Vgat_Position_Temp Where Dwxh = In_Dwxh;
          If v_shbj = '1' Then
             Return -1;
          Elsif v_shbj = '0' Then
             If v_dwzt1 = '1' Then --修改待审核
                Return 4;
             ElsIf v_dwzt1 = '2' Then --停用状态下的修改待审核
                Return 12;
             ElsIf v_dwzt1 = '3' Then --报废待审核
                If v_dwzt = '1' Then
                   Return 5;           --在用状态下的报废申请，也属于在用状态
                ElsIf v_dwzt = '2' Then
                   Return 6;           --停用状态下的报废申请，也属于停用状态
                Else
                   Return -1;
                End If;
             Else
                Return -1;
             End If;
          Elsif v_shbj = '2' Then
             If v_dwzt1 = '1' Then --修改审核不通过,也是在用状态
                Return 7;
             ElsIf v_dwzt1 = '2' Then --停用状态下的修改审核不通过
                Return 13;
             ElsIf v_dwzt1 = '3' Then --报废审核不通过
                If v_dwzt = '1' Then
                   Return 8;           --在用状态下的报废审核未通过，也属于在用状态
                ElsIf v_dwzt = '2' Then
                   Return 9;           --停用状态下的报废审核未通过，也属于停用状态
                Else
                   Return -1;
                End If;
             Else
                Return -1;
             End If;
          Else
            Return -1;
          End If;
       ElsIf Icount2 = 0 Then  --临时表没有数据，处于审核通过状态
          Select Dwzt Into v_dwzt From Dev_Vgat_Position Where Dwxh = In_Dwxh;
          If v_dwzt = '1' Then     --在用状态/审核通过
             Return 1;
          ElsIf v_dwzt = '2' Then  --停用状态/审核通过
             Return 2;
          ElsIf v_dwzt = '3' Then  --报废状态/审核通过
             Return 3;
          Else
             Return -1;
          End If;
       Else
          Return -1;
       End If;
    End If;
  End;

