  Function GetRecSxjnum(In_Xh Varchar2) Return Number Is
    Icount Number;
    Vzt    Varchar2(1);
  Begin
    Select 1 Into Icount From Dual;
    Select Count(*) Into Icount From Dev_Vgat_Position Where Dwxh = In_Xh;
    If Icount > 0 Then
      Select Count(1) Into Icount From (
                      Select a.sxjbh,row_number() over (partition by a.sxjbh order by b.ccrq desc nulls last) rn
                      From Dev_Vgat_Position_Camera a,dev_vgat_posicam_record b
                      Where a.Dwxh = In_Xh And a.Dwzt = 1 And a.sxjbh=b.sxjbh And b.ccbj ='1' And b.zt='1') Where rn = 1;
    Else
      Select Count(*) Into Icount From Dev_Vgat_Position_Jg Where Dwxh = In_Xh;
      If Icount > 0 Then
         Select Count(1) Into Icount From (
                      Select a.sxjbh,row_number() over (partition by a.sxjbh order by b.ccrq desc nulls last) rn
                      From Dev_Vgat_Position_Camera_Jg a,dev_vgat_posicam_record b
                      Where a.Dwxh = In_Xh And a.Dwzt = 1 And a.sxjbh=b.sxjbh And b.ccbj ='1' And b.zt='1') Where rn = 1;
      End If;
    End If;
    Return Icount;
