  --保存视频网关备案表对象
  Procedure Savedev_Vgat
  (
    Out_Jg Out Number, Out_Msg Out Varchar2
  ) Is
    Dev_Vgat_Obj Dev_Vgat%Rowtype;
    Rm_Log_Obj   Rm_Log%Rowtype;
    Icount       Number;
  Begin
    Rm_Log_Obj := Rm_Pub_Pkg.Getrm_Log;
    Rm_Pub_Pkg.Insertrm_Log(Rm_Log_Obj);
    Dev_Vgat_Obj := Vgat_Setdata_Pkg.Getdev_Vgat;

    --删除相同网关编号的停用网关
    Delete From Dev_Vgat Where Wgbh = Dev_Vgat_Obj.Wgbh And Wgzt = '2';
    Delete From Dev_Vgat Where Wgip = Dev_Vgat_Obj.Wgip And Wgzt = '2';

    Select Count(*)
    Into Icount
    From Dev_Vgat
    Where Glbm = Dev_Vgat_Obj.Glbm And Wgbh != Dev_Vgat_Obj.Wgbh And Wgzt = '1';
    If Icount > 0 Then
      Out_Jg := 2;
      Out_Msg := '同一管理部门只能有一个网关';
      Return;
    End If;

    Select Count(*)
    Into Icount
    From Dev_Vgat
    Where Wgip = Dev_Vgat_Obj.Wgip And Wgbh != Dev_Vgat_Obj.Wgbh And Wgzt = '1';
    If Icount > 0 Then
      Out_Jg := 3;
      Out_Msg := '网关IP冲突';
      Return;
    End If;
    Dev_Vgat_Obj.Wgzt := '1';
    Dev_Vgat_Obj.Jyw := Getvgatjyw(Dev_Vgat_Obj.Wgbh, Dev_Vgat_Obj.Sbcs, Dev_Vgat_Obj.Wgxlh, Dev_Vgat_Obj.Wgip,
                                   Dev_Vgat_Obj.Glbm, Dev_Vgat_Obj.Wgzt);
    Select Count(*) Into Icount From Dev_Vgat Where Wgbh = Dev_Vgat_Obj.Wgbh;
    If Icount = 0 Then
      Insert Into Dev_Vgat
        (Wgbh, Wgmc, Glbm, Bmjb, Sbcs, Wgxlh, Wgip, Bdfwyzm, Ydfwyzm, Jcptfwip, Jcptfwdk, Jcptjkxlh, Ptdk, Sbdk, Bdkjdk,
         Ydkjdk, Cjjg, Cjsj, Gxsj, Wgzt, Jyw, Bz, Csbj, Bjcsbj)
      Values
        (Dev_Vgat_Obj.Wgbh, Dev_Vgat_Obj.Wgmc, Dev_Vgat_Obj.Glbm, Dev_Vgat_Obj.Bmjb, Dev_Vgat_Obj.Sbcs,
         Dev_Vgat_Obj.Wgxlh, Dev_Vgat_Obj.Wgip, Dev_Vgat_Obj.Bdfwyzm, Dev_Vgat_Obj.Ydfwyzm, Dev_Vgat_Obj.Jcptfwip,
         Dev_Vgat_Obj.Jcptfwdk, Dev_Vgat_Obj.Jcptjkxlh, Dev_Vgat_Obj.Ptdk, Dev_Vgat_Obj.Sbdk, Dev_Vgat_Obj.Bdkjdk,
         Dev_Vgat_Obj.Ydkjdk, Dev_Vgat_Obj.Cjjg, Sysdate, Sysdate, Dev_Vgat_Obj.Wgzt, Dev_Vgat_Obj.Jyw, Dev_Vgat_Obj.Bz,
         '0', '0');
      Out_Jg := 1;
      Out_Msg := 'success';
    Else
      Update Dev_Vgat
      Set Wgmc = Dev_Vgat_Obj.Wgmc, Glbm = Dev_Vgat_Obj.Glbm, Bmjb = Dev_Vgat_Obj.Bmjb, Sbcs = Dev_Vgat_Obj.Sbcs,
          Wgxlh = Dev_Vgat_Obj.Wgxlh, Wgip = Dev_Vgat_Obj.Wgip, Bdfwyzm = Dev_Vgat_Obj.Bdfwyzm,
          Ydfwyzm = Dev_Vgat_Obj.Ydfwyzm, Jcptfwip = Dev_Vgat_Obj.Jcptfwip, Jcptfwdk = Dev_Vgat_Obj.Jcptfwdk,
          Jcptjkxlh = Dev_Vgat_Obj.Jcptjkxlh, Ptdk = Dev_Vgat_Obj.Ptdk, Sbdk = Dev_Vgat_Obj.Sbdk,
          Bdkjdk = Dev_Vgat_Obj.Bdkjdk, Ydkjdk = Dev_Vgat_Obj.Ydkjdk, Cjjg = Dev_Vgat_Obj.Cjjg, Gxsj = Sysdate,
          Wgzt = Dev_Vgat_Obj.Wgzt, Jyw = Dev_Vgat_Obj.Jyw, Bz = Dev_Vgat_Obj.Bz, Csbj = '0', Bjcsbj = '0'
