  --保存摄像机
  Procedure Savedev_Vgat_Camera
  (
    Out_Jg Out Number, Out_Msg Out Varchar2
  ) Is
    v_Dev_Vgat_Pos_Camera_Obj_Temp Dev_Vgat_Position_Camera_Temp%Rowtype;
    v_Icount                Number;
    v_Icount2               Number;
    v_Icount3               Number;
    v_Icount4                Number;
    v_Icount5                Number;
    v_Icount6                Number;
    v_Icount7                Number;
    v_Glbm                  Dev_Vgat_Position_Temp.Glbm%Type;
    v_Xh                    Dev_Vgat_Position_Camera_Temp.Xh%Type;
    v_Dwzt                  Dev_Vgat_Position_Camera.Dwzt%Type;
  Begin
    v_Dev_Vgat_Pos_Camera_Obj_Temp := Vgat_Setdata_Pkg.Getdev_Vgat_Pos_Camera_Temp();
    Select Count(1) Into v_Icount3 From Dev_Vgat_Position_Camera Where Sxjbh = v_Dev_Vgat_Pos_Camera_Obj_Temp.Sxjbh And Dwzt <> '3';
    Select Count(1) Into v_Icount4 From Dev_Vgat_Position_Camera_Temp Where Sxjbh = v_Dev_Vgat_Pos_Camera_Obj_Temp.Sxjbh And Dwzt <> '3';
    v_Xh := v_Dev_Vgat_Pos_Camera_Obj_Temp.Xh;
    If v_Xh Is Null Or v_Xh = '' Then--没有序号信息，是新增操作
      If v_Icount3 > 0 Or v_Icount4 > 0 Then   --存在相同编号的摄像机
          Out_Jg := 0;
          Out_Msg := '已存在编号为'||v_Dev_Vgat_Pos_Camera_Obj_Temp.Sxjbh||'的摄像机';
      Else
          If v_Dev_Vgat_Pos_Camera_Obj_Temp.Dwzt = '3' Then --是尚未备案就删除,忽略该操作
             Out_Jg := 1;
             Out_Msg := '1';
          Else
          --生成点位序号
            --v_Dev_Vgat_Pos_Camera_Obj_Temp.Xh := Frm_Comm_Pkg.Getseqval('SEQ_DEV_VGAT_POS_CAMERA_XH');
            Select Count(1) Into v_Icount7 From dev_vgat_position_temp Where Dwxh = v_Dev_Vgat_Pos_Camera_Obj_Temp.Dwxh;
            If v_Icount7 >0 Then
               Select glbm Into v_Glbm From dev_vgat_position_temp Where Dwxh = v_Dev_Vgat_Pos_Camera_Obj_Temp.Dwxh;
            Else
               Select Count(1) Into v_Icount7 From dev_vgat_position Where Dwxh = v_Dev_Vgat_Pos_Camera_Obj_Temp.Dwxh;
               If v_Icount7 >0 Then
                  Select glbm Into v_Glbm From dev_vgat_position Where Dwxh = v_Dev_Vgat_Pos_Camera_Obj_Temp.Dwxh;
               Else
                  Out_Jg := 0;
                  Out_Msg := '找不到点位';
                  Return;
               End If;
            End If;
            v_Dev_Vgat_Pos_Camera_Obj_Temp.Xh :=v_Glbm||Frm_Comm_Pkg.Getseqval('SEQ_DEV_VGAT_POS_CAMERA_XH');
            v_Dev_Vgat_Pos_Camera_Obj_Temp.Dwzt := '1';--新增摄像机，点位状态一定是在用态
            AddCamera(v_Dev_Vgat_Pos_Camera_Obj_Temp);
            Out_Jg := 1;
            Out_Msg := v_Dev_Vgat_Pos_Camera_Obj_Temp.Xh;
          End If;
      End IF;
    Else--有序号信息，是编辑，但也有可能Temp表中数据被清除（修改审核通过时）
      Select Count(1) Into v_Icount From Dev_Vgat_Position_Camera Where Xh = v_Xh And Dwzt <> '3';
      Select Count(1) Into v_Icount2 From Dev_Vgat_Position_Camera_Temp Where Xh = v_Xh;
      Select Count(1) Into v_Icount5 From Dev_Vgat_Position_Camera Where Xh <> v_Xh And Sxjbh = v_Dev_Vgat_Pos_Camera_Obj_Temp.Sxjbh And Dwzt <> '3';
      Select Count(1) Into v_Icount6 From Dev_Vgat_Position_Camera_Temp Where Xh <> v_Xh And Sxjbh = v_Dev_Vgat_Pos_Camera_Obj_Temp.Sxjbh And Dwzt <> '3';
      If v_Icount5 > 0 Or v_Icount6 > 0 Then --存在相应摄像机编号的摄像机,有可能发生在这种情况：尝试将一个摄像机编号变更为一个已存在的编号
         Out_Jg := 0;
         Out_Msg := '已存在编号为'||v_Dev_Vgat_Pos_Camera_Obj_Temp.Sxjbh||'的摄像机';
      Else --正常，可以修改摄像机信息（无论主表有无数据，都应当修改临时表，不允许直接修改主表）
         Select Dwzt Into v_Dwzt From Dev_Vgat_Position_Temp Where Dwxh = v_Dev_Vgat_Pos_Camera_Obj_Temp.Dwxh;
         If v_Dwzt = '2' Or v_Dwzt = '3' Then--点位是停用或报废的，摄像机肯定也是停用
            v_Dev_Vgat_Pos_Camera_Obj_Temp.Dwzt := v_Dwzt;
         End If;
         If v_Icount2 = 0 Then--若临时表数据被清除，新增
            AddCamera(v_Dev_Vgat_Pos_Camera_Obj_Temp);
         Else
            UpdateCamera(v_Dev_Vgat_Pos_Camera_Obj_Temp);
         End If;
         Out_Jg := 1;
         Out_Msg := v_Dev_Vgat_Pos_Camera_Obj_Temp.Xh;
      End If;
    End If;
  End;

