    Select Count(1) Into v_Icount From Dev_Vgat_Position Where Dwxh = v_Dev_Vgat_Position_Obj.Dwxh;
    If v_Icount = 0 Then --主表中不存在，停用是针对主表的操作，实际上，在java层控制不会进入该分支
       Out_Jg := 0;
       Out_Msg := '不存在指定点位';
    ElsIf v_Icount = 1 Then --主表中存在记录
       Select Dwzt Into v_Dwzt From Dev_Vgat_Position Where Dwxh = v_Dev_Vgat_Position_Obj.Dwxh;
       If v_Dwzt = '1' Then--在用状态点位可以停用，一旦停用，点位上的摄像机全部停用
          Update Dev_Vgat_Position Set Dwzt = '2',Csbj = '0',Bjcsbj = '0' Where Dwxh = v_Dev_Vgat_Position_Obj.Dwxh;
          Update Dev_Vgat_Position_Camera Set Dwzt = '2',Csbj = '0',Bjcsbj = '0' Where Dwxh = v_Dev_Vgat_Position_Obj.Dwxh And Dwzt <> '3';
          Update Dev_Vgat_Position_Temp Set Dwzt = '2',Shbj = '1' Where Dwxh = v_Dev_Vgat_Position_Obj.Dwxh;
          Update Dev_Vgat_Position_Camera_Temp Set Dwzt = '2' Where Dwxh = v_Dev_Vgat_Position_Obj.Dwxh And Dwzt <> '3';
          Delete From Dev_Vgat_Position_Temp Where Dwxh = v_Dev_Vgat_Position_Obj.Dwxh;
          Delete From Dev_Vgat_Position_Camera_Temp Where Dwxh = v_Dev_Vgat_Position_Obj.Dwxh;
          Out_Jg := 1;
          Out_Msg := v_Dev_Vgat_Position_Obj.Dwxh;
       Elsif v_Dwzt = '2' Then  --停用的不允许再次停用
          Out_Jg := 0;
          Out_Msg := '该点位已停用';
       Elsif v_Dwzt = '3' Then  --报废的不允许停用
          Out_Jg := 0;
          Out_Msg := '该点位已报废';
       Else
          Out_Jg := 0;
          Out_Msg := '摄像机点位信息有误，点位序号是：'||v_Dev_Vgat_Position_Obj.Dwxh;
       End If;
    Else
       Out_Jg := 0;
       Out_Msg := '摄像机点位信息有误，点位序号是：'||v_Dev_Vgat_Position_Obj.Dwxh;
    End If;
  End;

