  ) Is
    v_Dev_Vgat_Position_Obj  Dev_Vgat_Position_Temp%Rowtype;
    v_Icount                 Number;
    v_Icount2                Number;
    v_Icount3                Number;
    v_Dwzt                   Dev_Vgat_Position_Temp.Dwzt%Type;
    v_Pos                    Dev_Vgat_Position%Rowtype;
    Type caremaT Is Table Of Dev_Vgat_Position_Camera%Rowtype;
