    Select Count(1) Into v_Icount From Dev_Vgat_Position Where Dwxh = v_Dev_Vgat_Position_Obj.Dwxh;
    Select Count(1) Into v_Icount2 From Dev_Vgat_Position_Temp Where Dwxh = v_Dev_Vgat_Position_Obj.Dwxh;
    If v_Icount = 0 Then --主表中不存在，也就是尚未备案成功，直接干掉临时表的数据
       If v_Icount2 = 0 Then --临时表也不存在，java层控制不会进入此分支
          Out_Jg := 0;
          Out_Msg := '摄像机点位信息有误，点位序号是：'||v_Dev_Vgat_Position_Obj.Dwxh;
