  --点位审核
  Procedure Checkdev_Vgat_Position
  (
    Out_Jg Out Number, Out_Msg Out Varchar2
  ) Is
    v_Dev_Vgat_Position_Obj Dev_Vgat_Position_Temp%Rowtype;
    Type caremaT Is Table Of Dev_Vgat_Position_Camera_Temp%Rowtype;
    v_caremaTable           caremaT;
    v_Icount                Number;
    v_Icount2               Number;
    v_Icount3               Number;
    v_Dwzt                  Dev_Vgat_Position_Temp.Dwzt%Type;
    v_Shjg                  Dev_Vgat_Position.Shjg%Type;
    v_Shr                   Dev_Vgat_Position.Shr%Type;
    v_Shsj                  Dev_Vgat_Position.Shsj%Type;
    v_Sxjzt                 Dev_Vgat_Position_Camera.Dwzt%Type;
  Begin
    v_Dev_Vgat_Position_Obj := Vgat_Setdata_Pkg.Getdev_Vgat_Position;
    v_Dev_Vgat_Position_Obj.Shsj := Sysdate;

    Select Count(1) Into v_Icount From Dev_Vgat_Position Where Dwxh = v_Dev_Vgat_Position_Obj.Dwxh;
    Select Count(1) Into v_Icount2 From Dev_Vgat_Position_Temp Where Dwxh = v_Dev_Vgat_Position_Obj.Dwxh And shbj = '0';
    If v_Icount = 0 Then --主表中不存在，也就是尚未备案成功
       If v_Icount2 = 0 Then --临时表也不存在，java层控制不会进入此分支
          Out_Jg := 0;
          Out_Msg := '摄像机点位信息有误，点位序号是：'||v_Dev_Vgat_Position_Obj.Dwxh;
       Else --备案的审核，不通过，临时表shbj置2，通过，临时表审核标记置1，临时表进主表,临时表数据删除！！！
          If v_Dev_Vgat_Position_Obj.Shbj = '2' Then
            Update Dev_Vgat_Position_Temp
            Set Shbj = v_Dev_Vgat_Position_Obj.Shbj, Shsj = v_Dev_Vgat_Position_Obj.Shsj,
            Shyj = v_Dev_Vgat_Position_Obj.Shyj, Shjg = v_Dev_Vgat_Position_Obj.Shjg
            Where Dwxh = v_Dev_Vgat_Position_Obj.Dwxh;
            Out_Jg := 1;
            Out_Msg := v_Dev_Vgat_Position_Obj.Dwxh;
          ElsIf v_Dev_Vgat_Position_Obj.Shbj = '1' Then
              Update Dev_Vgat_Position_Temp
              Set Shbj = v_Dev_Vgat_Position_Obj.Shbj, Shsj = v_Dev_Vgat_Position_Obj.Shsj,
              Shyj = v_Dev_Vgat_Position_Obj.Shyj, Shjg = v_Dev_Vgat_Position_Obj.Shjg
              Where Dwxh = v_Dev_Vgat_Position_Obj.Dwxh;

              Insert Into Dev_Vgat_Position
              (Dwxh, Glbm, Splx, Spfl, Xzqh, Dllx, Glxzdj, Dldm, Lkh, Dlms, Szdd, Jd, Wd, Jrfs, Zjjrlx, Wgbh, Wgsqm, Wlwz,
               Ptbh, Kkbh, Hphm, Hpzl, Jybh, Wrjbh, Cjjg, Cjr, Cjsj, Gxsj, Shsj, Bz, Dwzt, Csbj, Bjcsbj, Sjxzqh, Shjg, Shr)
              Select Dwxh, Glbm, Splx, Spfl, Xzqh, Dllx, Glxzdj, Dldm, Lkh, Dlms, Szdd, Jd, Wd, Jrfs, Zjjrlx, Wgbh, Wgsqm,
                     Wlwz, Ptbh, Kkbh, Hphm, Hpzl, Jybh, Wrjbh, Cjjg, Cjr, Cjsj, Sysdate, Sysdate, Bz, '1', '0', '0', Sjxzqh,
                     v_Dev_Vgat_Position_Obj.Shjg, v_Dev_Vgat_Position_Obj.Shr
              From Dev_Vgat_Position_Temp a
              Where a.Dwxh = v_Dev_Vgat_Position_Obj.Dwxh;
              --清空点位临时表
              Delete From Dev_Vgat_Position_Temp Where Dwxh = v_Dev_Vgat_Position_Obj.Dwxh;

              Select Count(1) Into v_Icount3 From Dev_Vgat_Position_Camera
              Where Dwzt <> '3'
              And Sxjbh In (Select Sxjbh From Dev_Vgat_Position_Camera_Temp a Where a.Dwxh = v_Dev_Vgat_Position_Obj.Dwxh And Dwzt <> 3);
              If v_Icount3 > 0 Then --数据状态异常，实际上，再向Temp表添加的时候已经判断过了，此处加入此分支，以防逻辑有遗漏
                 Out_Jg := 0;
                 Out_Msg := '摄像机数据状态异常,审核操作中止,点位序号:'||v_Dev_Vgat_Position_Obj.Dwxh;
              Else
                Insert Into Dev_Vgat_Position_Camera
                (Xh, Dwxh, Sxjbh, Bhlx, Sxjmc, Jkfx, Bmqbh, Sxjcs, Sxjpp, Sxjxh, Sxjip, Sxjdk, Sxjtdh, Sxjfwyh, Sxjfwmm, Sxjfwymdz,
                 Ptzkz, Txcc, Cxys, Jglx, Mllx, Ytlx, Lmtbmlx, Lxbmlx, Csbj, Bjcsbj, Sjjcbj, Dwzt)
                Select Xh, Dwxh, Sxjbh, Bhlx, Sxjmc, Jkfx, Bmqbh, Sxjcs, Sxjpp, Sxjxh, Sxjip, Sxjdk, Sxjtdh, Sxjfwyh, Sxjfwmm,
                       Sxjfwymdz, Ptzkz, Txcc, Cxys, Jglx, Mllx, Ytlx, Lmtbmlx, Lxbmlx, '0', '0', Sjjcbj, Dwzt
                From Dev_Vgat_Position_Camera_Temp a
                Where a.Dwxh = v_Dev_Vgat_Position_Obj.Dwxh and Dwzt <> '3';
                --清空摄像机临时表
                Delete From Dev_Vgat_Position_Camera_Temp Where Dwxh = v_Dev_Vgat_Position_Obj.Dwxh;
                Out_Jg := 1;
                Out_Msg := v_Dev_Vgat_Position_Obj.Dwxh;
              End If;
          Else
            Out_Jg := 0;
            Out_Msg := '审核结果不正确：'||v_Dev_Vgat_Position_Obj.Shbj;
          End If;
       End IF;
    ElsIf v_Icount = 1 Then --主表中存在记录
       If v_Icount2 = 0 Then --临时表也不存在，java层控制不会进入此分支,按正常逻辑不会进入
          Out_Jg := 0;
          Out_Msg := '摄像机点位信息有误，点位序号是：'||v_Dev_Vgat_Position_Obj.Dwxh;
       Else
         Select Dwzt Into v_Dwzt From Dev_Vgat_Position_Temp Where Dwxh = v_Dev_Vgat_Position_Obj.Dwxh And Shbj = '0';
         If v_Dwzt = '1' Then--修改的审核，不通过，临时表审核标记置2，主表不变，若审核通过，临时表进主表
            If v_Dev_Vgat_Position_Obj.Shbj = '2' Then
