  --摄像机信息审核
  Procedure Savedev_Vgat_Position_Sh
  (
    Out_Jg Out Number, Out_Msg Out Varchar2
  ) Is
    Dev_Vgat_Position_Obj Dev_Vgat_Position_Temp%Rowtype;
    Icount                Number;
  Begin
    Dev_Vgat_Position_Obj := Vgat_Setdata_Pkg.Getdev_Vgat_Position;
    Dev_Vgat_Position_Obj.Shsj := Sysdate;

    If Dev_Vgat_Position_Obj.Shbj = '2' Then
      Update Dev_Vgat_Position_Temp
      Set Shbj = Dev_Vgat_Position_Obj.Shbj, Shsj = Dev_Vgat_Position_Obj.Shsj, Shyj = Dev_Vgat_Position_Obj.Shyj
      Where Dwxh = Dev_Vgat_Position_Obj.Dwxh;
    End If;
    If Dev_Vgat_Position_Obj.Shbj = '1' Then

      Delete From Dev_Vgat_Position a Where a.DwXh = Dev_Vgat_Position_Obj.DwXh;

      Insert Into Dev_Vgat_Position
        (DwXh, Glbm, Splx, Spfl, Xzqh, Dllx, Glxzdj, Dldm, Lkh, Dlms, Szdd, Jd, Wd, Jrfs, Zjjrlx, Wgbh, Wgsqm, Wlwz,
         Ptbh, Kkbh, Hphm, Hpzl, Jybh, Wrjbh, Cjjg, Cjsj, Gxsj, Shsj, Bz, DwZt, Csbj, Bjcsbj, Sjxzqh)
        Select DwXh, Glbm, Splx, Spfl, Xzqh, Dllx, Glxzdj, Dldm, Lkh, Dlms, Szdd, Jd, Wd, Jrfs, Zjjrlx, Wgbh, Wgsqm,
               Wlwz, Ptbh, Kkbh, Hphm, Hpzl, Jybh, Wrjbh, Cjjg, Cjsj, Sysdate, Sysdate, Bz, '1', '0', '0', Sjxzqh
        From Dev_Vgat_Position_Temp a
        Where a.DwXh = Dev_Vgat_Position_Obj.DwXh;

      Update Dev_Vgat_Position_Camera Set Dwzt = '0', Csbj = '0', Bjcsbj = '0' Where DwXh = Dev_Vgat_Position_Obj.DwXh;
      Delete Dev_Vgat_Position_Camera
      Where Sxjbh In (Select Sxjbh From Dev_Vgat_Position_Camera_Temp a Where a.DwXh = Dev_Vgat_Position_Obj.DwXh);

      Insert Into Dev_Vgat_Position_Camera
        (DwXh, Sxjbh, Bhlx, Sxjmc, Jkfx, Bmqbh, Sxjcs, Sxjpp, Sxjxh, Sxjip, Sxjdk, Sxjtdh, Sxjfwyh, Sxjfwmm, Sxjfwymdz,
         Ptzkz, Txcc, Cxys, Jglx, Mllx, Ytlx, Lmtbmlx, Lxbmlx, Csbj, Bjcsbj, Sjjcbj, Dwzt)
        Select DwXh, Sxjbh, Bhlx, Sxjmc, Jkfx, Bmqbh, Sxjcs, Sxjpp, Sxjxh, Sxjip, Sxjdk, Sxjtdh, Sxjfwyh, Sxjfwmm,
               Sxjfwymdz, Ptzkz, Txcc, Cxys, Jglx, Mllx, Ytlx, Lmtbmlx, Lxbmlx, '0', '0', Sjjcbj, Dwzt
        From Dev_Vgat_Position_Camera_Temp a
        Where a.DwXh = Dev_Vgat_Position_Obj.DwXh;

      Delete From Dev_Vgat_Position_Temp a Where a.DwXh = Dev_Vgat_Position_Obj.DwXh;
      Delete From Dev_Vgat_Position_Camera_Temp a Where a.DwXh = Dev_Vgat_Position_Obj.DwXh;
    End If;
    Out_Jg := 1;
    Out_Msg := Dev_Vgat_Position_Obj.DwXh;
  End;

  --删除点位信息
  Procedure Deletedev_Vgat_Position
  (
    Out_Jg Out Number, Out_Msg Out Varchar2
  ) Is
    Dev_Vgat_Position_Obj Dev_Vgat_Position_Temp%Rowtype;
  Begin
    Dev_Vgat_Position_Obj := Vgat_Setdata_Pkg.Getdev_Vgat_Position;

    If Dev_Vgat_Position_Obj.Shbj = '1' Then
      Update Dev_Vgat_Position
      Set DwZt = Dev_Vgat_Position_Obj.Bz, Zfsj = Sysdate, Csbj = '0', Bjcsbj = '0'
