      Where DwXh = Dev_Vgat_Position_Obj.DwXh;
      Delete From Dev_Vgat_Position_Temp Where DwXh = Dev_Vgat_Position_Obj.DwXh;
      Delete From Dev_Vgat_Position_Camera_Temp Where DwXh = Dev_Vgat_Position_Obj.DwXh;
