  Begin
    Dev_Vgat_Posicam_Apply_Obj := Vgat_Setdata_Pkg.Getdev_Vgat_Posicam_Apply;
    Dev_Vgat_Posicam_Apply_Obj.Gxsj := Sysdate;
    Select Count(*) Into Icount From Dev_Vgat_Posicam_Apply Where Xh = Dev_Vgat_Posicam_Apply_Obj.Xh;
    If Icount = 0 Then
      Delete From Dev_Vgat_Posicam_Apply a Where a.Xh = Dev_Vgat_Posicam_Apply_Obj.Xh;
      --生成主键
      Dev_Vgat_Posicam_Apply_Obj.Xh := Substr(Dev_Vgat_Posicam_Apply_Obj.Sqbm, 1, 6) ||
                                       Frm_Comm_Pkg.Getseqlenval('SEQ_DEV_VGAT_POSICAM_APPLY', 6);
      --生成校验位
      Dev_Vgat_Posicam_Apply_Obj.Jyw := Frm_Sys_Pkg.Encrypt(Dev_Vgat_Posicam_Apply_Obj.Xh ||
                                                            Dev_Vgat_Posicam_Apply_Obj.Dwbh ||
                                                            Dev_Vgat_Posicam_Apply_Obj.Sqbm ||
                                                            Dev_Vgat_Posicam_Apply_Obj.Spbm,
                                                            Dev_Vgat_Posicam_Apply_Obj.Lczt);

      Insert Into Dev_Vgat_Posicam_Apply
        (Xh, Dwbh, Dwlx, Sqbm, Qsdfzjg, Spbm, Spdfzjg, Lczt, Jyw, Gxsj, Bz)
      Values
        (Dev_Vgat_Posicam_Apply_Obj.Xh, Dev_Vgat_Posicam_Apply_Obj.Dwbh, Dev_Vgat_Posicam_Apply_Obj.Dwlx,
         Dev_Vgat_Posicam_Apply_Obj.Sqbm, Dev_Vgat_Posicam_Apply_Obj.Qsdfzjg, Dev_Vgat_Posicam_Apply_Obj.Spbm,
         Dev_Vgat_Posicam_Apply_Obj.Spdfzjg, Dev_Vgat_Posicam_Apply_Obj.Lczt, Dev_Vgat_Posicam_Apply_Obj.Jyw,
         Dev_Vgat_Posicam_Apply_Obj.Gxsj, Dev_Vgat_Posicam_Apply_Obj.Bz);
      Out_Jg := 1;
      Out_Msg := Dev_Vgat_Posicam_Apply_Obj.Xh;
    Else
      --生成校验位
      Dev_Vgat_Posicam_Apply_Obj.Jyw := Frm_Sys_Pkg.Encrypt(Dev_Vgat_Posicam_Apply_Obj.Xh ||
                                                            Dev_Vgat_Posicam_Apply_Obj.Dwbh ||
                                                            Dev_Vgat_Posicam_Apply_Obj.Sqbm ||
                                                            Dev_Vgat_Posicam_Apply_Obj.Spbm,
                                                            Dev_Vgat_Posicam_Apply_Obj.Lczt);
      Update Dev_Vgat_Posicam_Apply
      Set Dwbh = Dev_Vgat_Posicam_Apply_Obj.Dwbh, Dwlx = Dev_Vgat_Posicam_Apply_Obj.Dwlx,
          Sqbm = Dev_Vgat_Posicam_Apply_Obj.Sqbm, Qsdfzjg = Dev_Vgat_Posicam_Apply_Obj.Qsdfzjg,
          Spbm = Dev_Vgat_Posicam_Apply_Obj.Spbm, Spdfzjg = Dev_Vgat_Posicam_Apply_Obj.Spdfzjg,
          Lczt = Dev_Vgat_Posicam_Apply_Obj.Lczt, Jyw = Dev_Vgat_Posicam_Apply_Obj.Jyw,
          Gxsj = Dev_Vgat_Posicam_Apply_Obj.Gxsj, Bz = Dev_Vgat_Posicam_Apply_Obj.Bz
      Where Xh = Dev_Vgat_Posicam_Apply_Obj.Xh;
      Out_Jg := 1;
      Out_Msg := Dev_Vgat_Posicam_Apply_Obj.Xh;
    End If;

    v_Xtyxms := Frm_Comm_Pkg.Getsysparavalue('60', 'XTYXMS');
    If v_Xtyxms = '2' Or v_Xtyxms = '3' Then
      -- 如果dwlx=3,表示申请外省的信息，这个要上传部局,定义T6071:6071
      If Dev_Vgat_Posicam_Apply_Obj.Dwlx = '3' Then
        --如果状态为待审批,更新BZ信息为SPDFZJG,即表示发给审批地接收
        If Dev_Vgat_Posicam_Apply_Obj.Lczt = '2' Then
          Update Dev_Vgat_Posicam_Apply
          Set Bz = Dev_Vgat_Posicam_Apply_Obj.Spdfzjg
          Where Xh = Dev_Vgat_Posicam_Apply_Obj.Xh;
          --说明审批完成,发给申请地
        Else
          Update Dev_Vgat_Posicam_Apply
          Set Bz = Dev_Vgat_Posicam_Apply_Obj.Qsdfzjg
          Where Xh = Dev_Vgat_Posicam_Apply_Obj.Xh;
        End If;
        Frm_Comm_Pkg.Writeupqueue(Null, '6071', Dev_Vgat_Posicam_Apply_Obj.Xh);
        -- 否则，下发给支队，表示审批结果通过还是不通过T6041:6042
      Else
        If v_Xtyxms = '3' Then
          Delete From Dev_Dmid_Vgat_Posicam_Apply Where Xh = Dev_Vgat_Posicam_Apply_Obj.Xh;
          Insert Into Dev_Dmid_Vgat_Posicam_Apply
            (Xh, Dwbh, Dwlx, Sqbm, Qsdfzjg, Spbm, Spdfzjg, Lczt, Jyw, Gxsj, Bz)
          Values
            (Dev_Vgat_Posicam_Apply_Obj.Xh, Dev_Vgat_Posicam_Apply_Obj.Dwbh, Dev_Vgat_Posicam_Apply_Obj.Dwlx,
             Dev_Vgat_Posicam_Apply_Obj.Sqbm, Dev_Vgat_Posicam_Apply_Obj.Qsdfzjg, Dev_Vgat_Posicam_Apply_Obj.Spbm,
             Dev_Vgat_Posicam_Apply_Obj.Spdfzjg, Dev_Vgat_Posicam_Apply_Obj.Lczt, Dev_Vgat_Posicam_Apply_Obj.Jyw,
             Dev_Vgat_Posicam_Apply_Obj.Gxsj, Dev_Vgat_Posicam_Apply_Obj.Bz);
        End If;
      End If;
      -- 支队的发给总队审核T6031
    Elsif v_Xtyxms = '1' Then
      Frm_Comm_Pkg.Writeupqueue(Null, '6032', Dev_Vgat_Posicam_Apply_Obj.Xh);
      -- 部局的收到消息后，判断审批状态，如果没通过直接下发给总队，如果通过或者作废除了下发给支队之外还生成一条control表的信息下发给总队定义为T6081:6081
    Elsif v_Xtyxms = '4' Then
      Delete From Dev_Dmid_Vgat_Posicam_Apply Where Xh = Dev_Vgat_Posicam_Apply_Obj.Xh;
      Insert Into Dev_Dmid_Vgat_Posicam_Apply
        (Xh, Dwbh, Dwlx, Sqbm, Qsdfzjg, Spbm, Spdfzjg, Lczt, Jyw, Gxsj, Bz)
      Values
        (Dev_Vgat_Posicam_Apply_Obj.Xh, Dev_Vgat_Posicam_Apply_Obj.Dwbh, Dev_Vgat_Posicam_Apply_Obj.Dwlx,
         Dev_Vgat_Posicam_Apply_Obj.Sqbm, Dev_Vgat_Posicam_Apply_Obj.Qsdfzjg, Dev_Vgat_Posicam_Apply_Obj.Spbm,
         Dev_Vgat_Posicam_Apply_Obj.Spdfzjg, Dev_Vgat_Posicam_Apply_Obj.Lczt, Dev_Vgat_Posicam_Apply_Obj.Jyw,
         Dev_Vgat_Posicam_Apply_Obj.Gxsj, Dev_Vgat_Posicam_Apply_Obj.Bz);
    End If;
  End;

