CREATE OR REPLACE Package Body Zhpt_Download_Pkg Is

	Type Tt_Type Is Table Of Varchar2(3000) Index By Binary_Integer;

	--获取存储过程版本
	Function Get_Ver Return Varchar2 Is
	Begin
		--Return '2014-03-14.1';
		Return '2016-03-31.1';
	End;

	Function Toarray
	(
		Liststr In Varchar2, V1 Out Tt_Type
	) Return Integer Is
		Tmpstr Varchar2(3000);
		Str    Varchar2(3000);
		j      Integer;
	Begin
		Str := Liststr;
		j := 0;
		If Instr(Liststr, ',', 1, 1) = 0 Then
			V1(j) := Liststr;
			j := j + 1;
		Else
			While Instr(Str, ',', 1, 1) > 0 Loop
				Tmpstr := Substr(Str, 1, Instr(Str, ',', 1, 1) - 1);

				V1(j) := Tmpstr;
				Str := Substr(Str, Instr(Str, ',', 1, 1) + 1, Length(Str));
				j := j + 1;
			End Loop;
			If Not Str Is Null Then
				--将最后一个保存
				V1(j) := Str;
				j := j + 1;
			End If;
		End If;
		Return j;
	End;

