	Procedure Insertzhpt_Veh_Vio
	(
		Zhpt_Veh_Vio_Base_Obj1 Zhpt_Veh_Vio_Base%Rowtype, Rtn Out Number, Rtncolname Out Varchar2, Rtnmsg Out Varchar2
	) Is
		Icount    Number;
		Tempxcdjg Zhpt_Veh_Vio.Xcdjg%Type;
	Begin

		Select Count(1)
		Into Icount
		From Zhpt_Veh_Vio
		Where Hpzl = Zhpt_Veh_Vio_Base_Obj1.Hpzl And Hphm = Zhpt_Veh_Vio_Base_Obj1.Hphm And
					To_Char(Scsj, 'yyyy-mm-dd') >= To_Char(Zhpt_Veh_Vio_Base_Obj1.Scsj, 'yyyy-mm-dd');

		--已存在新记录时不再写数据
		If Icount > 0 Then
			Rtn := 1;
			Return;
		End If;

		--将历史未布控的记录写为无效
		Update Zhpt_Veh_Vio
		Set Sfyx = '0'
		Where Hpzl = Zhpt_Veh_Vio_Base_Obj1.Hpzl And Hphm = Zhpt_Veh_Vio_Base_Obj1.Hphm And
					To_Char(Scsj, 'yyyy-mm-dd') < To_Char(Zhpt_Veh_Vio_Base_Obj1.Scsj, 'yyyy-mm-dd') And Bkbj = '0' And
					Sfyx = '1';

		If Zhpt_Veh_Vio_Base_Obj1.Bkzlx = '2' Then
			--外省车
			Select Count(1) Into Icount From Frm_Department Where Glbm = Zhpt_Veh_Vio_Base_Obj1.Glbm;
			If Icount = 0 Then
				Rtn := 0;
				Rtnmsg := '外省车违法未处理glbm无法找到对应的发证机关';
				Return;
			Else
				Select Fzjg Into Tempxcdjg From Frm_Department Where Glbm = Zhpt_Veh_Vio_Base_Obj1.Glbm;
			End If;
		Else
			Tempxcdjg := Zhpt_Veh_Vio_Base_Obj1.Fzjg;
		End If;

		Insert Into Zhpt_Veh_Vio
			(Xcid, Hpzl, Hphm, Fzjg, Glbm, Xzqh, Clpp, Cllx, Csys, Syxz, Wfjls, Wfxzqh, Bksjly, Bkzlx, Bkbj, Bksj, Bkjg, Bkxh,
			 Ckbj, Ckjg, Cksj, Cklx, Ckfs, Scsj, Gxsj, Xcdjg, Sfyx, Bz)
		Values
			(Vmc_Comm_Pkg.Getxcid, Zhpt_Veh_Vio_Base_Obj1.Hpzl, Zhpt_Veh_Vio_Base_Obj1.Hphm, Zhpt_Veh_Vio_Base_Obj1.Fzjg,
			 Zhpt_Veh_Vio_Base_Obj1.Glbm, Zhpt_Veh_Vio_Base_Obj1.Xzqh, Zhpt_Veh_Vio_Base_Obj1.Clpp,
			 Zhpt_Veh_Vio_Base_Obj1.Cllx, Zhpt_Veh_Vio_Base_Obj1.Csys, Zhpt_Veh_Vio_Base_Obj1.Syxz,
			 Zhpt_Veh_Vio_Base_Obj1.Wfjls, Zhpt_Veh_Vio_Base_Obj1.Wfxzqh, '1', Zhpt_Veh_Vio_Base_Obj1.Bkzlx, '0', Null, Null,
			 Null, '0', Null, Null, Null, Null, Zhpt_Veh_Vio_Base_Obj1.Scsj, Zhpt_Veh_Vio_Base_Obj1.Gxsj, Tempxcdjg, '1',
			 Zhpt_Veh_Vio_Base_Obj1.Bz);
		Rtn := 1;
	End;

