			Select Count(1)
			Into Icount
			From Zhpt_Veh_Uncheck_Base
			Where Xh = Zhpt_Veh_Uncheck_Base_Obj.Xh And Substr(Tjyf, 1, 6) = Substr(Zhpt_Veh_Uncheck_Base_Obj.Tjyf, 1, 6) And
						Fxny Is Not Null;
