		End If;

		--逾期未报废
		If In_Sjlx = '2' Then
			Setzhpt_Veh_Unlogout_Base(In_Jdcxh, In_Hpzl, In_Hphm, In_Fzjg, In_Xzqh, In_Clpp, In_Clxh, In_Cllx, In_Csys,
																In_Syxz, Null, Null, In_Glrq, In_Gxsj, In_Tjyf, Null, Null, Zhpt_Veh_Unlogout_Base_Obj);
