	--违法未处理
	Procedure Save_Zhpt_Veh_Vio_b
	(
		In_Id In Varchar2, In_Glbm In Varchar2, In_Sjlx In Varchar2, In_Hpzl In Varchar2, In_Hphm In Varchar2,
		In_Fzjg In Varchar2, In_Cllx In Varchar2, In_Clpp In Varchar2, In_Csys In Varchar2, In_Syxz In Varchar2,
		In_Wcls In Varchar2, In_Xzqh In Varchar2, In_Gxsj In Varchar2, In_Yxbj In Varchar2, In_Jmfbj In Varchar2,
		Rtn Out Number, Rtncolname Out Varchar2, Rtnmsg Out Varchar2
	) Is
		Zhpt_Veh_Vio_Base_Obj Zhpt_Veh_Vio_Base%Rowtype;
	Begin
		Setzhpt_Veh_Vio_Base(In_Hpzl, In_Hphm, In_Fzjg, In_Glbm, Null, In_Clpp, In_Cllx, Substr(In_Csys, 0, 5), In_Syxz,
												 In_Wcls, In_Xzqh, Null, In_Sjlx, In_Gxsj, Substr(In_Id, 1, 6), Null, In_Jmfbj,
												 Zhpt_Veh_Vio_Base_Obj);

		If Zhpt_Veh_Vio_Base_Obj.Bkzlx Not In ('3') Then
			Rtn := 0;
			Rtncolname := 'sjlx';
			Rtnmsg := '下载的记录sjlx不符合规则';
			Return;
		End If;

		Insertzhpt_Veh_Vio_Base(Zhpt_Veh_Vio_Base_Obj);
		If Zhpt_Veh_Vio_Base_Obj.Syxz In ('B', 'E', 'R', 'O', 'P', 'Q', 'S') Then
			Insertzhpt_Veh_Vio_Out(Zhpt_Veh_Vio_Base_Obj);
			Commit;
		End If;

		--货运
		If Zhpt_Veh_Vio_Base_Obj.Syxz = 'F' Then
			If Zhpt_Veh_Vio_Base_Obj.Wfjls > 4 Or Zhpt_Veh_Vio_Base_Obj.Jmfbj = '1' Then
				Insertzhpt_Veh_Vio_Out(Zhpt_Veh_Vio_Base_Obj);
				Commit;
			End If;
		End If;

		--小微型面包车
		If Zhpt_Veh_Vio_Base_Obj.Cllx = 'K39' Or Zhpt_Veh_Vio_Base_Obj.Cllx = 'K49' Then
			If Zhpt_Veh_Vio_Base_Obj.Wfjls > 4 Or Zhpt_Veh_Vio_Base_Obj.Jmfbj = '1' Then
				Insertzhpt_Veh_Vio_Out(Zhpt_Veh_Vio_Base_Obj);
				Commit;
			End If;
		End If;
		Rtn := 1;
	End;

