	Function Inner_Updatealltable
	(
		In3 Varchar2, In4 Varchar2
	) Return Varchar2 Is
		I_Len1    Number;
		I_Len2    Number;
		I_Mid     Number;
		i         Number;
		S_Procstr Varchar2(4096);
		S_Result  Varchar2(32);
		B_Tmp     Raw(2048);
		B_Tmp1    Raw(2048);
		B_Result  Raw(2048);
	Begin
		B_Tmp := Utl_Raw.Cast_To_Raw(In3);
		I_Len1 := Utl_Raw.Length(B_Tmp);
		B_Tmp1 := Utl_Raw.Cast_To_Raw(In4);
		I_Len2 := Utl_Raw.Length(B_Tmp1);
		I_Mid := I_Len1 Mod I_Len2;
		I_Len2 := Trunc(I_Len1 / I_Len2);
		If I_Len2 > 0 Then
			B_Tmp1 := Utl_Raw.Copies(B_Tmp1, I_Len2);
			If I_Mid > 0 Then
				B_Tmp1 := Utl_Raw.Concat(B_Tmp1, Utl_Raw.Substr(B_Tmp1, 1, I_Mid));
			End If;
		Else
			B_Tmp1 := Utl_Raw.Substr(B_Tmp1, 1, I_Mid);
		End If;
		B_Result := Utl_Raw.Bit_Xor(B_Tmp, B_Tmp1);
		S_Procstr := Rawtohex(B_Result);
		I_Len1 := Length(S_Procstr);
		If I_Len1 < 32 Then
			For i In 1 .. 32 - I_Len1 Loop
				I_Mid := I_Len1 - (i Mod I_Len1 - 1);
				I_Mid := Ascii(Substr(S_Procstr, I_Mid, 1)); --加入已有数据变数
				I_Mid := I_Mid * i Mod 10; --加入循环变数
				S_Procstr := S_Procstr || To_Char(I_Mid);
			End Loop;
		Else
			For i In 1 .. Length(S_Procstr) - 32 Loop
				I_Mid := i Mod I_Len1;
				I_Mid := Ascii(Substr(S_Procstr, I_Mid, 1));
				I_Mid := (I_Mid * i) Mod I_Len1;
				If I_Mid = 0 Then
					I_Mid := I_Len1;
				End If;
				S_Procstr := Substr(S_Procstr, 1, I_Mid - 1) || Substr(S_Procstr, I_Mid + 1);
				I_Len1 := I_Len1 - 1;
			End Loop;
		End If;
		--根据和值 移位处理
		I_Mid := 0;
		For i In 1 .. 32 Loop
			I_Mid := I_Mid + Ascii(Substr(S_Procstr, i, 1));
		End Loop;
		I_Mid := I_Mid Mod 36;
		If I_Mid > 0 Then
			S_Result := '';
			For i In 1 .. 36 Loop
				I_Len1 := (i + I_Mid - 1) Mod 36;
				If I_Len1 = 0 Then
					I_Len1 := 36;
				End If;
				S_Result := S_Result || Substr(S_Procstr, I_Len1, 1);
			End Loop;
		Else
			S_Result := S_Procstr;
		End If;
		Return S_Result;
	End;
